  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of informations
-- ----------------------------
INSERT INTO `informations` VALUES ('1', 'Giới Thiệu', 'Gioi-Thieu', '<p>Trường mầm non Bảo Ngọc được th&agrave;nh lập c&aacute;ch đ&acirc;y 17 năm, trải qua qu&aacute; tr&igrave;nh phấn dấu suốt 17 năm qua, trường ch&uacute;ng t&ocirc;i kh&ocirc;ng ngừng lớn mạnh về mọi mặt, đ&aacute;p ứng được y&ecirc;u cầu ng&agrave;y c&agrave;ng cao của đ&ocirc;ng đảo tầng lớp phụ huynh trong khu vực v&agrave; phụ huynh của c&aacute;c Quận kh&aacute;c xa trường về học. Sĩ số học sinh của năm học sau, bao giờ cũng cao hơn năm học trước.</p>\r\n\r\n<p>Trường Mầm Non Bảo Ngọc nằm tr&ecirc;n địa b&agrave;n phường H&ograve;a Thuận T&acirc;y thuộc khu d&acirc;n cư số 1 Nguyễn Tri Phương Quận Hải Ch&acirc;u &ndash; TP Đ&agrave; Nẵng . Trường gồm hai cơ sở : Cơ sở 1 số 12 đường Lương Nhữ Hộc, cơ sở 2 số 144 đường Tiểu La.</p>\r\n\r\n<p>Đến với trường MN Bảo Ngọc , Qu&iacute; vị sẽ đến với địa chỉ đ&aacute;ng tin cậy- Nh&agrave; trường sẽ đ&aacute;p ứng được mọi mong mỏi của qu&iacute; vị. Ch&uacute;ng t&ocirc;i sẽ tiếp tục phấn đấu về mọi mặt để ng&agrave;y c&agrave;ng l&agrave;m cho qu&iacute; vị được h&agrave;i l&ograve;ng hơn.</p>\r\n\r\n<p>Trường MN Bảo Ngọc nằm ở trung t&acirc;m khu d&acirc;n cư , c&oacute; đường giao th&ocirc;ng thuận lợi. Diện t&iacute;ch của trường l&agrave; : 1.500.000 m2&nbsp;<span style=\"background-color:rgb(246, 245, 237); color:rgb(102, 102, 102); font-family:patrick hand,cursive; font-size:15px\">&nbsp;gồm 18 ph&ograve;ng học, 2 s&acirc;n chơi&nbsp;v&agrave; 2 bếp ăn / 2 cơ sở</span></p>\r\n', '<div class=\"about-us-content\">\r\n<p>Trường mầm non Bảo Ngọc được th&agrave;nh lập c&aacute;ch đ&acirc;y 17 năm, trải qua qu&aacute; tr&igrave;nh phấn dấu suốt 17 năm qua, trường ch&uacute;ng t&ocirc;i kh&ocirc;ng ngừng lớn mạnh về mọi mặt, đ&aacute;p ứng được y&ecirc;u cầu ng&agrave;y c&agrave;ng cao của đ&ocirc;ng đảo tầng lớp phụ huynh trong khu vực v&agrave; phụ huynh của c&aacute;c Quận kh&aacute;c xa trường về học. Sĩ số học sinh của năm học sau, bao giờ cũng cao hơn năm học trước.</p>\r\n\r\n<p>Trường Mầm Non Bảo Ngọc nằm tr&ecirc;n địa b&agrave;n phường H&ograve;a Thuận T&acirc;y thuộc khu d&acirc;n cư số 1 Nguyễn Tri Phương Quận Hải Ch&acirc;u &ndash; TP Đ&agrave; Nẵng . Trường gồm hai cơ sở : Cơ sở 1 số 12 đường Lương Nhữ Hộc, cơ sở 2 số 144 đường Tiểu La.</p>\r\n\r\n<p>Đến với trường MN Bảo Ngọc , Qu&iacute; vị sẽ đến với địa chỉ đ&aacute;ng tin cậy- Nh&agrave; trường sẽ đ&aacute;p ứng được mọi mong mỏi của qu&iacute; vị. Ch&uacute;ng t&ocirc;i sẽ tiếp tục phấn đấu về mọi mặt để ng&agrave;y c&agrave;ng l&agrave;m cho qu&iacute; vị được h&agrave;i l&ograve;ng hơn.</p>\r\n\r\n<p>Trường MN Bảo Ngọc nằm ở trung t&acirc;m khu d&acirc;n cư , c&oacute; đường giao th&ocirc;ng thuận lợi. Diện t&iacute;ch của trường l&agrave; : 1.500.000 m2 gồm 18 ph&ograve;ng học, 2 s&acirc;n chơi v&agrave; 2 bếp ăn / 2 cơ sở.Trường được thiết kế tho&aacute;ng, m&aacute;t, đẹp, đầu đủ &aacute;nh s&aacute;ng trời cho c&aacute;c ph&ograve;ng học.</p>\r\n\r\n<p>Nh&agrave; Trường đ&atilde; đầu tư c&aacute;c trang thiết bị hiện đại phục vụ cho việc dạy v&agrave; học. Ở mỗi ph&ograve;ng học của trẻ c&oacute; : cầu thang m&aacute;y,điều h&ograve;a, nước n&oacute;ng,Ti vi,m&aacute;y ph&aacute;t điện, Camera phục vụ cho việc quản l&yacute; của nh&agrave; trường, c&oacute; giường c&aacute; nh&acirc;n v&agrave; đồ d&ugrave;ng cho mỗi trẻ ri&ecirc;ng biệt, c&oacute; đầy đủ đồ d&ugrave;ng học tập,đồ chơi &hellip;ph&ugrave; hợp với lứa tuổi mầm non.</p>\r\n\r\n<p>Đặc biệt ch&uacute;ng t&ocirc;i c&oacute; đội ngũ c&aacute;n bộ quản l&yacute; v&agrave; gi&aacute;o vi&ecirc;n m&agrave; khi xa trường qu&iacute; vị sẽ lu&ocirc;n nhớ m&atilde;i về t&igrave;nh cảm của ch&uacute;ng t&ocirc;i đ&atilde; d&agrave;nh cho qu&iacute; vị.</p>\r\n</div>\r\n', '99ce690d6f8b9aa91407b34e636dc158.jpg', '1402390320', '0');

-- ----------------------------
-- Table structure for `news`
-- ----------------------------
DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
