  `created` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=48 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of news
-- ----------------------------
INSERT INTO `news` VALUES ('11', 'Nấu cháo cho con ngon hơn ngoài hàng', 'Nau-chao-cho-con-ngon-hon-ngoai-hang', '2', 'Ăn mãi cơm, nui, mỳ, miến... chồng và con chắc hẳn cũng chán rồi! Vậy nên, mẹ học đôi ba cách nấu cháo ngon \'đổi vị\' cho cả gia đình nào.\r\nHãy \'khởi động\' đầu tiên với món cháo gà nhé!', '<p style=\"text-align:justify\">Ăn m&atilde;i cơm, nui, mỳ, miến... chồng v&agrave; con chắc hẳn cũng ch&aacute;n rồi! Vậy n&ecirc;n, mẹ học đ&ocirc;i ba c&aacute;ch nấu ch&aacute;o ngon &#39;đổi vị&#39; cho cả gia đ&igrave;nh n&agrave;o.</p>\r\n\r\n<p style=\"text-align:justify\">H&atilde;y &#39;khởi động&#39; đầu ti&ecirc;n với m&oacute;n ch&aacute;o g&agrave; nh&eacute;!</p>\r\n\r\n<p style=\"text-align:justify\"><strong>Ch&aacute;o k&ecirc; thịt g&agrave;</strong></p>\r\n\r\n<p style=\"text-align:justify\"><em>Nguy&ecirc;n liệu&nbsp;(cho 4 người ăn)</em></p>\r\n\r\n<p style=\"text-align:justify\">- &frac12; con g&agrave; - 2 phần k&ecirc;, 1 phần gạo tẻ, 1 nắm gạo nếp. - H&agrave;nh l&aacute;, gia vị&hellip;</p>\r\n\r\n<p style=\"text-align:justify\"><em>C&aacute;ch chế biến</em></p>\r\n\r\n<p style=\"text-align:justify\">- G&agrave; rửa sạch, cho v&agrave;o nồi luộc ch&iacute;n.</p>\r\n\r\n<p style=\"text-align:justify\">- Gạo tẻ v&agrave; gạo nếp vo sạch, để r&aacute;o. K&ecirc; nhặt sạch sạn v&agrave; vo sạch.</p>\r\n\r\n<p style=\"text-align:justify\">- G&agrave; ch&iacute;n vớt ra để nguội. Nấu gạo tẻ, gạo nếp v&agrave; k&ecirc; với nước d&ugrave;ng g&agrave; vừa luộc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>Ch&aacute;o g&agrave; nấm hương</strong></p>\r\n\r\n<p><em>Nguy&ecirc;n liệu</em></p>\r\n\r\n<p>50gr gạo; 4 ch&eacute;n nước; 30 gr thịt g&agrave; nạc; 30gr nấm rơm; 1 muỗng nhỏ dầu ăn; h&agrave;nh; gia vị...</p>\r\n\r\n<p><em>C&aacute;ch chế biến</em></p>\r\n\r\n<p>- Vo gạo sạch rồi đem nấu th&agrave;nh ch&aacute;o.</p>\r\n\r\n<p>- Nấm hương được rửa sạch, ng&acirc;m trong nước cho mềm sau đ&oacute; vớt ra để r&aacute;o v&agrave; th&aacute;i nhỏ.</p>\r\n\r\n<p>- Thịt g&agrave; v&agrave; nấm xắt thật nhỏ rồi x&agrave;o chung với h&agrave;nh v&agrave; gia vị. N&ecirc;m một ch&uacute;t muối.</p>\r\n\r\n<p>- Cho hỗn hợp g&agrave; x&agrave;o nấm hương v&agrave;o ch&aacute;o đ&atilde; ninh nhừ rồi đun s&ocirc;i trở lại. Th&ecirc;m 1 th&igrave;a dầu ăn, trộn đều tr&ecirc;n bếp l&agrave; ho&agrave;n th&agrave;nh.</p>\r\n\r\n<p>M&oacute;n ăn n&agrave;y thơm m&ugrave;i nấm v&agrave; c&oacute; vị ngọt b&eacute;o dễ chịu sẽ khiến b&eacute; m&ecirc; t&iacute;t đ&acirc;y. Ngo&agrave;i ra, nấm l&agrave; nguồn đạm thực vật rất tốt cho c&aacute;c b&eacute; đấy c&aacute;c mẹ ạ.</p>\r\n\r\n<p><strong>Ch&aacute;o g&agrave; b&iacute; đỏ</strong></p>\r\n\r\n<p><em>Nguy&ecirc;n liệu</em></p>\r\n\r\n<p>- Thịt g&agrave;; gạo tẻ; gạo nếp</p>\r\n\r\n<p>- B&iacute; đỏ; dầu đậu n&agrave;nh; nước</p>\r\n\r\n<p><em>C&aacute;ch chế biến</em></p>\r\n\r\n<p>- Rửa sạch thịt g&agrave; rồi đem luộc ch&iacute;n.</p>\r\n\r\n<p>- D&ugrave;ng nước luộc g&agrave; ninh nhừ gạo th&agrave;nh ch&aacute;o. Tỷ lệ gạo tẻ/gạo nếp l&agrave; 1/1.</p>\r\n\r\n<p>- X&eacute; thịt g&agrave; đ&atilde; luộc rồi xay nhỏ hoặc băm nhuyễn.</p>\r\n\r\n<p>- B&iacute; đỏ nạo vỏ v&agrave; rửa sạch, sau đ&oacute; luộc ch&iacute;n. Mẹ nhớ luộc &iacute;t nước để giữ vị ngọt của b&iacute; đỏ nh&eacute;. Khi b&iacute; đỏ ch&iacute;n, lấy ra v&agrave; d&ugrave;ng th&igrave;a m&agrave;i nhuyễn.</p>\r\n\r\n<p>- Cho thịt g&agrave; c&ugrave;ng b&iacute; đỏ đ&atilde; chuẩn bị v&agrave;o ch&aacute;o, đ&aacute;nh đều, đun s&ocirc;i. Th&ecirc;m 1 th&igrave;a dầu ăn, đảo đều tr&ecirc;n bếp l&agrave; ho&agrave;n th&agrave;nh xong m&oacute;n ch&aacute;o thịt g&agrave; nấu b&iacute; đỏ cho b&eacute; rồi.</p>\r\n', '542b3448c2e6cf3f5f185da9325f09e4.jpg', '1405520961');
INSERT INTO `news` VALUES ('12', 'Tắm yến mạch con hết sạch...thủy đậu ', 'Tam-yen-mach-con-het-sach-thuy-dau', '2', 'Lo con mắc sởi chưa xong thì mẹ con em đã “vấp” ngay phải thủy đậu. Xuka nhà em cả tuần liền cứ sốt với bỏ ăn suốt, người thì nổi đầy mụn nước, nhìn thương ơi là thương. Em thì lại chưa bao giờ bị cả nên cũng chẳng có kinh nghiệm gì, thấy con như vậy cứ cuống cả lên vì lo lắng. Cũng may có chị bạn là y tá chỉ dẫn cách chăm sóc con, nếu không chắc em sẽ chẳng biết làm thế nào nữa.\r\n\r\nGiờ con khỏe hẳn rồi, em mới thở phào nhẹ nhõm. Xuka chơi ngoan được nên em tranh thủ chia sẻ vài kinh nghiệm chăm con khi bị thủy đậu với các mẹ, để nhỡ may bé nào bị mắc bệnh thì mẹ cũng bớt lo. Thực ra, thủy đậu cũng là bệnh nhẹ thôi nên nếu mẹ biết chăm sóc đúng cách thì bé sẽ rất mau khỏi. Quan trọng nhất là mẹ phải giữ cho da con luôn sạch sẽ, và tránh để bé gãi nhiều, nếu không tình hình sẽ càng trầm trọng hơn.', '<p>Lo con mắc sởi chưa xong th&igrave; mẹ con em đ&atilde; &ldquo;vấp&rdquo; ngay phải thủy đậu. Xuka nh&agrave; em cả tuần liền cứ sốt với bỏ ăn suốt, người th&igrave; nổi đầy mụn nước, nh&igrave;n thương ơi l&agrave; thương. Em th&igrave; lại chưa bao giờ bị cả n&ecirc;n cũng chẳng c&oacute; kinh nghiệm g&igrave;, thấy con như vậy cứ cuống cả l&ecirc;n v&igrave; lo lắng. Cũng may c&oacute; chị bạn l&agrave; y t&aacute; chỉ dẫn c&aacute;ch chăm s&oacute;c con, nếu kh&ocirc;ng chắc em sẽ chẳng biết l&agrave;m thế n&agrave;o nữa.</p>\r\n\r\n<p>Giờ con khỏe hẳn rồi, em mới thở ph&agrave;o nhẹ nh&otilde;m. Xuka chơi ngoan được n&ecirc;n em tranh thủ chia sẻ v&agrave;i kinh nghiệm chăm con khi bị thủy đậu với c&aacute;c mẹ, để nhỡ may b&eacute; n&agrave;o bị mắc bệnh th&igrave; mẹ cũng bớt lo. Thực ra, thủy đậu cũng l&agrave; bệnh nhẹ th&ocirc;i n&ecirc;n nếu mẹ biết chăm s&oacute;c đ&uacute;ng c&aacute;ch th&igrave; b&eacute; sẽ rất mau khỏi. Quan trọng nhất l&agrave; mẹ phải giữ cho da con lu&ocirc;n sạch sẽ, v&agrave; tr&aacute;nh để b&eacute; g&atilde;i nhiều, nếu kh&ocirc;ng t&igrave;nh h&igrave;nh sẽ c&agrave;ng trầm trọng hơn.</p>\r\n\r\n<p>C&oacute; điều, khi bị thủy đậu b&eacute; sẽ bị ngứa ng&aacute;y rất kh&oacute; chịu, n&ecirc;n kh&ocirc;ng tr&aacute;nh khỏi chuyện g&atilde;i được. Vậy n&ecirc;n em phải &aacute;p dụng &ldquo;chi&ecirc;u&rdquo; giảm ngứa bằng c&aacute;ch nấu nước yến mạch cho Xuka tắm, hiệu quả lắm c&aacute;c mẹ ạ!</p>\r\n\r\n<p>Theo lời &ldquo;m&aacute;ch nước&rdquo; của chị bạn, em đun s&ocirc;i chừng 2 cốc bột yến mạch với 2 l&iacute;t nước. C&aacute;c mẹ chỉ cần đun khoảng 20 ph&uacute;t l&agrave; được, sau đ&oacute; d&ugrave;ng vải sạch lọc lấy nước v&agrave; pha v&agrave;o chậu tắm cho con. Phần nước tắm em cũng cẩn thận đun s&ocirc;i rồi đợi đến khi c&ograve;n &acirc;m ấm mới tắm cho b&eacute;. V&igrave; bột yến mạch c&oacute; t&aacute;c dụng giảm ngứa rất tốt, n&ecirc;n b&eacute; sẽ dễ chịu hơn nhiều v&agrave; kh&ocirc;ng c&ograve;n &ldquo;g&atilde;i xo&agrave;nh xoạch&rdquo; suốt ng&agrave;y nữa.</p>\r\n\r\n<p>Nhờ thế m&agrave; Xuka nh&agrave; em mau khỏi bệnh hơn rất nhiều. Chắc nhiều mẹ thắc mắc l&agrave; tại sao bị thủy đậu vẫn được tắm đ&uacute;ng kh&ocirc;ng? Thực ra, c&aacute;c b&aacute;c sĩ cũng khuy&ecirc;n l&agrave; kh&ocirc;ng n&ecirc;n ki&ecirc;ng tắm đ&acirc;u c&aacute;c mẹ. V&igrave; nếu kh&ocirc;ng được vệ sinh sạch sẽ, b&eacute; ngứa n&ecirc;n sẽ c&agrave;ng g&atilde;i nhiều, dẫn đến x&acirc;y xước v&agrave; nhiễm tr&ugrave;ng, thậm ch&iacute; l&agrave; g&acirc;y ra c&aacute;c biến chứng nặng nề hơn. V&igrave; thế h&agrave;ng ng&agrave;y mẹ vẫn n&ecirc;n lau rửa cho b&eacute;, chỉ cần d&ugrave;ng nước đ&atilde; đun ấm ở nơi k&iacute;n gi&oacute;, v&agrave; tắm nhanh một ch&uacute;t cho b&eacute; l&agrave; được. Để hiệu quả hơn, mẹ n&ecirc;n pha th&ecirc;m nước yến mạch v&agrave;o nước tắm như em đ&atilde; &aacute;p dụng cho b&eacute; ở nh&agrave;.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; kinh nghiệm em học được từ người bạn l&agrave;m y t&aacute;, gi&uacute;p hỗ trợ chữa bệnh thủy đậu cho b&eacute;. Tuy nhi&ecirc;n, đ&acirc;y kh&ocirc;ng phải l&agrave; thuốc chữa v&agrave; c&aacute;c mẹ cũng n&ecirc;n tham khảo &yacute; kiến b&aacute;c sĩ để &aacute;p dụng ph&ugrave; hợp cho b&eacute; y&ecirc;u của m&igrave;nh.</p>\r\n', '002e241a32498df98a0e89d372144a50.jpg', '1402385594');
INSERT INTO `news` VALUES ('13', 'Lá đậu ván đánh bay rôm sảy ', 'La-dau-van-danh-bay-rom-say', '2', 'Mùa hè sắp đến là lúc các mẹ cũng như các bé phải đối mặt với một kẻ thù mới, đó là các nốt rôm sảy mẩn ngứa xuất hiện trên da, nhất là những bé dưới 3 tuổi. Nguyên nhân gây ra hiện tượng này là do tuyến mồ hôi dưới da bé bị tắc nghẽn, dẫn đến việc da bị nổi các nốt đỏ gây ngứa ngáy khó chịu. Tuy đây là một triệu chứng không quá nghiêm trọng, nhưng nếu để lâu ngày không chữa trị có thể gây viêm da và các hậu quả khó lường khác, chưa kể việc bé ngứa ngáy và khó chịu quấy khóc khiến các mẹ cũng sốt ruột theo.\r\n\r\nMình sinh bé vào mùa đông, vì vậy cũng chẳng có chút kinh nghiệm nào về vấn đề này cả. Bé nhà mình nóng người, ham vận động, lại hay ra mồ hôi trộm, mình chỉ sợ vài tuần nữa vào hè mà nóng lên, thể nào bé cũng dính rôm sảy. Biết vậy nhưng mình cũng chỉ biết phòng trước cho con bằng cách mua vài hộp phấn rôm để đấy, chuẩn bị cho đợt nắng nóng sắp tới. Mua là vậy nhưng mình cũng không hoàn toàn thích dùng loại này vì bé nhà mình cũng nghịch, để bôi cho bé rất khó, và cũng sợ không khéo làm bột phấn bay vào mũi hay mồm con.', '<p>M&ugrave;a h&egrave; sắp đến l&agrave; l&uacute;c c&aacute;c mẹ cũng như c&aacute;c b&eacute; phải đối mặt với một kẻ th&ugrave; mới, đ&oacute; l&agrave; c&aacute;c nốt r&ocirc;m sảy mẩn ngứa xuất hiện tr&ecirc;n da, nhất l&agrave; những b&eacute; dưới 3 tuổi. Nguy&ecirc;n nh&acirc;n g&acirc;y ra hiện tượng n&agrave;y l&agrave; do tuyến mồ h&ocirc;i dưới da b&eacute; bị tắc nghẽn, dẫn đến việc da bị nổi c&aacute;c nốt đỏ g&acirc;y ngứa ng&aacute;y kh&oacute; chịu. Tuy đ&acirc;y l&agrave; một triệu chứng kh&ocirc;ng qu&aacute; nghi&ecirc;m trọng, nhưng nếu để l&acirc;u ng&agrave;y kh&ocirc;ng chữa trị c&oacute; thể g&acirc;y vi&ecirc;m da v&agrave; c&aacute;c hậu quả kh&oacute; lường kh&aacute;c, chưa kể việc b&eacute; ngứa ng&aacute;y v&agrave; kh&oacute; chịu quấy kh&oacute;c khiến c&aacute;c mẹ cũng sốt ruột theo.</p>\r\n\r\n<p>M&igrave;nh sinh b&eacute; v&agrave;o m&ugrave;a đ&ocirc;ng, v&igrave; vậy cũng chẳng c&oacute; ch&uacute;t kinh nghiệm n&agrave;o về vấn đề n&agrave;y cả. B&eacute; nh&agrave; m&igrave;nh n&oacute;ng người, ham vận động, lại hay ra mồ h&ocirc;i trộm, m&igrave;nh chỉ sợ v&agrave;i tuần nữa v&agrave;o h&egrave; m&agrave; n&oacute;ng l&ecirc;n, thể n&agrave;o b&eacute; cũng d&iacute;nh r&ocirc;m sảy. Biết vậy nhưng m&igrave;nh cũng chỉ biết ph&ograve;ng trước cho con bằng c&aacute;ch mua v&agrave;i hộp phấn r&ocirc;m để đấy, chuẩn bị cho đợt nắng n&oacute;ng sắp tới. Mua l&agrave; vậy nhưng m&igrave;nh cũng kh&ocirc;ng ho&agrave;n to&agrave;n th&iacute;ch d&ugrave;ng loại n&agrave;y v&igrave; b&eacute; nh&agrave; m&igrave;nh cũng nghịch, để b&ocirc;i cho b&eacute; rất kh&oacute;, v&agrave; cũng sợ kh&ocirc;ng kh&eacute;o l&agrave;m bột phấn bay v&agrave;o mũi hay mồm con.</p>\r\n\r\n<p>Cuối tuần trước để thay đổi kh&ocirc;ng kh&iacute;, nh&agrave; m&igrave;nh quyết định k&eacute;o nhau về qu&ecirc; chơi, kh&ocirc;ng ngờ chuyến đi n&agrave;y đ&atilde; cho m&igrave;nh những kinh nghiệm qu&yacute; gi&aacute; v&agrave; l&agrave;m cho mấy hộp phấn r&ocirc;m m&igrave;nh mua trước đ&oacute; bị cất v&agrave;o g&oacute;c tủ v&igrave; kh&ocirc;ng c&ograve;n cần thiết nữa.</p>\r\n\r\n<p>Nh&agrave; m&igrave;nh c&oacute; hai anh em, m&igrave;nh l&agrave; &uacute;t, anh đ&atilde; lấy vợ v&agrave; sống ở qu&ecirc;. M&igrave;nh v&agrave; chị d&acirc;u s&agrave;n s&agrave;n tuổi nhau, lại sinh em b&eacute; c&aacute;ch nhau c&oacute; một th&aacute;ng, n&ecirc;n hai chị em hay chia sẻ với nhau những kinh nghiệm từ l&uacute;c bầu b&iacute; đến khi sinh nở, v&agrave; b&acirc;y giờ l&agrave; nu&ocirc;i con. Nghe m&igrave;nh kể về vụ phấn r&ocirc;m, chị mới cười v&agrave; tr&aacute;ch m&igrave;nh kh&ocirc;ng n&oacute;i sớm, v&igrave; nh&agrave; chị c&oacute; một c&aacute;ch triệt để trị r&ocirc;m sảy m&agrave; kh&ocirc;ng cần sắm sửa phấn bột g&igrave; cả. Chị kể, b&eacute; nh&agrave; chị chưa v&agrave;o h&egrave; m&agrave; h&ocirc;m trước đ&atilde; mọc nốt r&ocirc;m đỏ li ti ở người, b&eacute; ngứa ng&aacute;y n&ecirc;n quấy kh&oacute;c với lấy tay g&atilde;i suốt. Thấy thế chị b&egrave;n ra vườn ngắt một &iacute;t l&aacute; c&acirc;y đậu v&aacute;n đun l&ecirc;n rồi chắt một &iacute;t nước pha v&agrave;o nước tắm của b&eacute;. Chỉ v&agrave;i h&ocirc;m tắm như vậy, chị kể c&aacute;c nốt r&ocirc;m bay đi sạch, da b&eacute; c&ograve;n s&iacute;t lại b&oacute;ng mượt như chưa hề c&oacute; chuyện g&igrave; xảy ra. M&igrave;nh cũng đ&atilde; từng nghe n&oacute;i rằng tắm l&aacute; cũng c&oacute; c&ocirc;ng dụng l&agrave;m m&aacute;t da b&eacute;, nhưng kh&ocirc;ng nghĩ rằng n&oacute; lại c&oacute; hiệu quả triệt để đến vậy. Khi về, chị đưa cho m&igrave;nh một t&uacute;i l&aacute; to đ&ugrave;ng kh&ocirc;ng qu&ecirc;n dặn d&ograve; phải tắm cho b&eacute; thường xuy&ecirc;n bằng nước l&aacute; n&agrave;y v&igrave; n&oacute; kh&ocirc;ng chỉ c&oacute; c&ocirc;ng dụng trị r&ocirc;m m&agrave; c&ograve;n gi&uacute;p ph&ograve;ng ngừa r&ocirc;m hiệu quả.</p>\r\n\r\n<p>Vậy l&agrave; nhờ c&oacute; chị d&acirc;u m&agrave; m&igrave;nh đ&atilde; bớt đi được một nỗi lo mỗi khi h&egrave; đến. M&igrave;nh xin chia sẻ c&aacute;ch chế biến l&aacute; n&agrave;y để l&agrave;m nước tắm để c&aacute;c mẹ thao khảo: c&aacute;c mẹ chỉ cần đun nước s&ocirc;i, bỏ một &iacute;t l&aacute; đậu v&aacute;n đ&atilde; rửa sạch v&agrave;o nồi nước v&agrave; đun th&ecirc;m khoảng 1 ph&uacute;t l&agrave; bắc ra được. Nước n&agrave;y c&aacute;c mẹ lọc bỏ l&aacute;, rồi ho&agrave; v&agrave;o nước tắm b&igrave;nh thường của b&eacute;, sao cho nhiệt độ nước tắm ở mức 37 độ l&agrave; được rồi. Tất nhi&ecirc;n khi &aacute;p dụng c&aacute;ch n&agrave;y, c&aacute;c mẹ n&ecirc;n t&igrave;m mua hoặc lấy l&aacute; ở một nguồn tin cậy để đảm bảo an to&agrave;n cho c&aacute;c con nh&eacute;.</p>\r\n', '8871157b96ffa77582c2939a0bea7050.jpg', '1402384291');
INSERT INTO `news` VALUES ('14', 'Xoá vết muỗi đốt cho con chân xinh ', 'Xoa-vet-muoi-dot-cho-con-chan-xinh', '2', 'Sinh con gái nên ngoài chuyện chăm con khoẻ, dạy con ngoan, em còn rất quan tâm Sinh con gái nên ngoài chuyện chăm con khoẻ, dạy con ngoan, em còn rất quan tâm đến việc giữ gìn vẻ bề ngoài xinh xắn. sạch sẽ cho bé. Cún nhà em hiện đang học mẫu giáo thôi nhưng em luôn có ý thức tránh nắng, giữ da trắng cho con, cho bé nuôi tóc dài và hướng dẫn con thường xuyên chải, tết tóc gọn gàng., váy áo cũng rất hợp thời, dễ thương. Có lẽ cũng vì vậy mà Cún khá xinh xắn, đi đâu cũng nổi bật.', '<p>Sinh con g&aacute;i n&ecirc;n ngo&agrave;i chuyện chăm con khoẻ, dạy con ngoan, em c&ograve;n rất quan t&acirc;m&nbsp;Sinh con g&aacute;i n&ecirc;n ngo&agrave;i chuyện chăm con khoẻ, dạy con ngoan, em c&ograve;n rất quan t&acirc;m&nbsp;đến việc giữ g&igrave;n vẻ bề ngo&agrave;i xinh xắn. sạch sẽ cho b&eacute;. C&uacute;n nh&agrave; em hiện đang học mẫu gi&aacute;o th&ocirc;i nhưng em lu&ocirc;n c&oacute; &yacute; thức tr&aacute;nh nắng, giữ da trắng cho con, cho b&eacute; nu&ocirc;i t&oacute;c d&agrave;i v&agrave; hướng dẫn con thường xuy&ecirc;n chải, tết t&oacute;c gọn g&agrave;ng., v&aacute;y &aacute;o cũng rất hợp thời, dễ thương. C&oacute; lẽ cũng v&igrave; vậy m&agrave; C&uacute;n kh&aacute; xinh xắn, đi đ&acirc;u cũng nổi bật.</p>\r\n\r\n<p>Một điều khiến em v&ocirc; c&ugrave;ng tự h&agrave;o ở C&uacute;n nữa, đ&oacute; l&agrave; con c&oacute; đ&ocirc;i ch&acirc;n d&agrave;i v&agrave; thon giống hệt mẹ. Tuy nhi&ecirc;n, kh&ocirc;ng hiểu sao C&uacute;n lại hay bị muỗi đốt nhất nh&agrave;. Nhất l&agrave; khi con bắt đầu đi học mẫu gi&aacute;o, ch&acirc;n C&uacute;n l&uacute;c về lu&ocirc;n lấm tấm những vết muỗi đốt. Vết n&agrave;y chưa khỏi vết kia đ&atilde; c&oacute;. Lo lắng cho con g&aacute;i sau n&agrave;y lớn l&ecirc;n sẽ bị &ldquo;ch&acirc;n hoa gấm&rdquo; rất mất thẩm mỹ n&ecirc;n em đ&atilde; cất c&ocirc;ng t&igrave;m hiểu rất nhiều mẹo vặt để xo&aacute; vết th&acirc;m do muỗi đốt để lại cho con.</p>\r\n\r\n<p>M&ugrave;a h&egrave; đến cũng l&agrave; thời gian muỗi ho&agrave;nh h&agrave;nh. Em xin m&aacute;ch chị em những mẹo xo&aacute; vết muỗi đốt m&agrave; bản than đ&atilde; trải nghiệm v&agrave; thấy c&oacute; kết quả tốt:</p>\r\n\r\n<p><strong>Sữa mẹ</strong></p>\r\n\r\n<p>Loại thực phẩm &ldquo;c&acirc;y nh&agrave; l&aacute; vườn&rdquo; nhưng lại c&oacute; t&aacute;c dụng cực tốt trong việc xo&aacute; mờ vết muỗi đốt. Với c&aacute;c b&eacute; trong độ tuổi c&ograve;n b&uacute; ti mẹ, khi con bị muối đốt th&agrave;nh nốt đỏ. Mẹ c&oacute;&nbsp;thể&nbsp;d&ugrave;ng sữa mẹ b&ocirc;i v&agrave;o chỗ muỗi đốt cho b&eacute;, mỗi ng&agrave;y khoảng 3-4 lần, 3 ng&agrave;y vết muỗi đốt sẽ hết v&agrave; kh&ocirc;ng để lại sẹo.</p>\r\n\r\n<p><strong>Nước chanh</strong></p>\r\n\r\n<p>Thoa nước chanh vắt từ quả chanh tươi v&agrave;o c&aacute;c nốt m&agrave;u th&acirc;m đen khi bị muỗi đốt v&agrave; massage v&agrave;o c&ugrave;ng da bị vết th&acirc;m do muỗi đốt của b&eacute;. Những vitamin C c&oacute; trong chanh sẽ gi&uacute;p tẩy trắng da tự nhi&ecirc;n, gi&uacute;p da mọc lại v&agrave; chữa l&agrave;nh vết sẹo một c&aacute;ch nhanh ch&oacute;ng.</p>\r\n\r\n<p><strong>Nước &eacute;p c&agrave; chua v&agrave; đu đủ</strong></p>\r\n\r\n<p>Để điều trị vết th&acirc;m v&agrave; sẹo do bị muỗi đốt, mẹ c&oacute; thể sử dụng nước &eacute;p c&agrave; chua v&agrave; đu đủ để gi&uacute;p loại bỏ ch&uacute;ng.</p>\r\n\r\n<p>Chỉ cần ch&agrave; nước &eacute;p c&agrave; chua v&agrave; đu đủ v&agrave;o c&aacute;c vết th&acirc;m v&agrave; sẹo do muỗi đốt v&agrave; để ch&uacute;ng lưu lại tr&ecirc;n da khoảng 10 ph&uacute;t v&agrave; rửa sạch.</p>\r\n\r\n<p>Để cho kết quả tốt nhất, em khuyến c&aacute;o c&aacute;c chị em h&atilde;y &aacute;p dụng biện ph&aacute;p n&agrave;y cho con 2 lần/ ng&agrave;y cho đến khi c&aacute;c vết th&acirc;m biến mất nh&eacute;. Ngo&agrave;i ra, l&agrave;n da của b&eacute; cũng sẽ s&aacute;ng l&ecirc;n tr&ocirc;ng thấy.</p>\r\n\r\n<p><strong>Ch&agrave; khoai t&acirc;y</strong></p>\r\n\r\n<p>Một biện ph&aacute;p tự nhi&ecirc;n kh&aacute;c chị em c&oacute; thể &aacute;p dụng khi con bị muỗi cắn l&agrave; ch&agrave; x&aacute;t một v&agrave;i l&aacute;t mỏng khoai t&acirc;y sống tr&ecirc;n l&agrave;n da của con cũng sẽ gi&uacute;p l&agrave;m s&aacute;ng hơn v&ugrave;ng da th&acirc;m v&agrave; nhiều sẹo n&agrave;y.</p>\r\n\r\n<p>Mẹ cũng c&oacute; thể nghiền một củ khoai t&acirc;y v&agrave; để trong tủ lạnh, Sau đ&oacute; th&ecirc;m một ch&uacute;t nước cốt chanh tươi v&agrave; &aacute;p dụng thoa nước khoai t&acirc;y bằng một miếng b&ocirc;ng v&agrave; rửa sạch sau 15 ph&uacute;t. Khoai t&acirc;y c&oacute; chứa c&aacute;c enzym rất hữu &iacute;ch trong việc mờ dần c&aacute;c vết sẹo tối m&agrave;u.</p>\r\n\r\n<p><strong>Vitamin E, dầu tầm xu&acirc;n hoặc bơ ca cao, gel l&ocirc; hội</strong></p>\r\n\r\n<p>Một biện ph&aacute;p đơn giản hơn nữa l&agrave; mẹ b&eacute; c&oacute; thể sử dụng tinh dầu nguy&ecirc;n chất Vitamin E, dầu tầm xu&acirc;n hoặc bơ ca cao, gel l&ocirc; hội v&agrave; &aacute;p dụng trực tiếp v&agrave;o những vết th&acirc;m cho b&eacute; khi bị muỗi đốt cũng sẽ l&agrave;m giảm sẹo, gi&uacute;p mờ dần dấu vết muỗi cắn. B&ecirc;n cạnh đ&oacute; ch&uacute;ng c&ograve;n rất tốt để l&agrave;m mềm v&agrave; giữ ẩm l&agrave;n da của b&eacute; nh&agrave; bạn nữa cơ.</p>\r\n\r\n<p><strong>Kem đ&aacute;nh răng</strong></p>\r\n\r\n<p>Mẹ b&eacute; c&oacute; thể&nbsp;b&ocirc;i kem đ&aacute;nh răng l&ecirc;n chỗ muỗi đốt. Nốt muỗi đốt sẽ lặn rất nhanh. C&aacute;ch n&agrave;y m&igrave;nh thấy nhiều mẹ biết v&agrave; hay &aacute;p dụng.</p>\r\n\r\n<p>Ngo&agrave;i những mẹo trị vết muỗi đốt, em cũng xin m&aacute;ch c&aacute;c mẹ c&aacute;c ph&ograve;ng tr&aacute;nh muỗi cho b&eacute;. &quot;Ph&ograve;ng bệnh hơn chữa bệnh&quot; vẫn đ&uacute;ng phải kh&ocirc;ng c&aacute;c mẹ!</p>\r\n', 'cd1cc94a442caae77617c36d102ea023.jpg', '1402384698');
INSERT INTO `news` VALUES ('15', 'Trẻ thích ngủ sấp sẽ thông minh hơn ', 'Tre-thich-ngu-sap-se-thong-minh-hon', '2', 'Theo nghiên cứu của một nhóm các chuyên gia của trường Đại học Harvard, những đứa trẻ thông minh và dễ đạt được thành công khi lớn một phần là do tư thế ngủ lúc nhỏ quyết định.\r\n\r\nTheo đó, nhóm chuyên gia đã tiến hành khảo sát tư thế ngủ của hơn 350 trẻ nhỏ, có sức khỏe và phát triển bình thường. Kết quả, những đứa trẻ yêu thích tư thế ngủ nằm sấp hoặc dang rộng chân tay thường có chỉ số IQ cao hơn, phản xạ tốt hơn những trẻ khác. Những trẻ thích ngủ ở tư thế nằm thẳng và dang rộng chân tay cũng có tốc độ phát triển trí tuệ tương đương.', '<p>Theo nghi&ecirc;n cứu của một nh&oacute;m c&aacute;c chuy&ecirc;n gia của trường Đại học Harvard, những đứa trẻ th&ocirc;ng minh v&agrave; dễ đạt được th&agrave;nh c&ocirc;ng khi lớn một phần l&agrave; do tư thế ngủ l&uacute;c nhỏ quyết định.</p>\r\n\r\n<p>Theo đ&oacute;, nh&oacute;m chuy&ecirc;n gia đ&atilde; tiến h&agrave;nh khảo s&aacute;t tư thế ngủ của hơn 350 trẻ nhỏ, c&oacute; sức khỏe v&agrave; ph&aacute;t triển b&igrave;nh thường. Kết quả, những đứa trẻ y&ecirc;u th&iacute;ch tư thế ngủ nằm sấp hoặc dang rộng ch&acirc;n tay thường c&oacute; chỉ số IQ cao hơn, phản xạ tốt hơn những trẻ kh&aacute;c. Những trẻ th&iacute;ch ngủ ở tư thế nằm thẳng v&agrave; dang rộng ch&acirc;n tay cũng c&oacute; tốc độ ph&aacute;t triển tr&iacute; tuệ tương đương.</p>\r\n\r\n<p>D&ugrave; tư thế ngủ sấp phần n&agrave;o đ&oacute; c&oacute; lợi cho sự ph&aacute;t triển tr&iacute; tuệ của trẻ, song c&aacute;c chuy&ecirc;n gia n&agrave;y cũng khuyến c&aacute;o rằng, đ&acirc;y l&agrave; thế ngủ nguy hiểm dễ &lsquo;lấy mạng&rsquo; trẻ em, đặc biệt l&agrave; b&eacute; sơ sinh v&igrave; nguy cơ dẫn đến nghẹt thở cao. <em>&ldquo;Với trẻ dưới 1 tuổi v&agrave; sơ sinh, phần đầu của b&eacute; to v&agrave; nặng, trong khi đ&oacute; sức đỡ của g&aacute;y chưa tốt n&ecirc;n b&eacute; kh&oacute; c&oacute; thể tự xoay m&igrave;nh. Do đ&oacute;, b&eacute; rất dễ bị nghẹt thở do chăn, gối chặn v&agrave;o mũi&rdquo;,</em> c&aacute;c chuy&ecirc;n gia n&oacute;i.</p>\r\n\r\n<p>Do vậy, trẻ sơ sinh n&ecirc;n nằm ngủ ở tư thế nghi&ecirc;ng b&ecirc;n phải v&agrave; đầu thấp xuống, ở dưới cổ n&ecirc;n đệm một khăn b&ocirc;ng nhỏ. Sau 1-2 giờ đổi tư thế nằm nghi&ecirc;ng sang b&ecirc;n kia nếu kh&ocirc;ng đầu trẻ c&oacute; thể bị biến dạng. Trong trường hợp trẻ vừa b&uacute; sữa no th&igrave; cần l&oacute;t chăn nằm nghi&ecirc;ng phải, để tr&aacute;nh khỏi n&ocirc;n trớ.</p>\r\n\r\n<p>Với trẻ nhỏ, tốt nhất l&agrave; n&ecirc;n để ngủ ở tư thế nằm ngửa, v&igrave; tư thế nằm n&agrave;y to&agrave;n bộ c&aacute;c phần cơ tr&ecirc;n người b&eacute; đều được thả lỏng, &aacute;p lực l&ecirc;n nội tạng như: tim, dạ d&agrave;y, đường ruột v&agrave; b&agrave;ng quang... l&agrave; rất &iacute;t.</p>\r\n', '840d5927e0aa74f7ade5d09dbe077def.jpg', '1402385014');
INSERT INTO `news` VALUES ('16', 'Bé mọc răng sớm ăn mấy cũng còi? ', 'Be-moc-rang-som-an-may-cung-coi', null, 'Từ khi sinh con, lại là con trai đầu lòng và cháu độc đinh của nhà nội, em luôn được nhà chồng chiều chuộng hết mức. Mọi công việc nội trợ gia đình đều giao tất tật cho người giúp việc, em chỉ việc chăm con khỏe mạnh. Những tưởng như vậy là đã sung sướng, nhưng khổ nỗi, việc chăm con em cũng làm không đến nơi đến chốn.\r\n\r\nEm cho con bú mẹ hoàn toàn, theo lý thuyết, đáng lẽ bé nhà em phải bụ bẫm mới đúng. Vậy mà đến gần 5 tháng, trông con vẫn còi còi và bé hơn các bé cùng tháng nhiều, tay chân cũng không chắc bằng các bạn. Vừa lo con không đủ chất, lại vừa sợ cả nhà nói mình không chăm được con, em đành cho bé ăn dặm sớm hơn bình thường.', '<p>Từ khi sinh con, lại l&agrave; con trai đầu l&ograve;ng v&agrave; ch&aacute;u độc đinh của nh&agrave; nội, em lu&ocirc;n được nh&agrave; chồng chiều chuộng hết mức. Mọi c&ocirc;ng việc nội trợ gia đ&igrave;nh đều giao tất tật cho người gi&uacute;p việc, em chỉ việc chăm con khỏe mạnh. Những tưởng như vậy l&agrave; đ&atilde; sung sướng, nhưng khổ nỗi, việc chăm con em cũng l&agrave;m kh&ocirc;ng đến nơi đến chốn.</p>\r\n\r\n<p>Em cho con b&uacute; mẹ ho&agrave;n to&agrave;n, theo l&yacute; thuyết, đ&aacute;ng lẽ b&eacute; nh&agrave; em phải bụ bẫm mới đ&uacute;ng. Vậy m&agrave; đến gần 5 th&aacute;ng, tr&ocirc;ng con vẫn c&ograve;i c&ograve;i v&agrave; b&eacute; hơn c&aacute;c b&eacute; c&ugrave;ng th&aacute;ng nhiều, tay ch&acirc;n cũng kh&ocirc;ng chắc bằng c&aacute;c bạn. Vừa lo con kh&ocirc;ng đủ chất, lại vừa sợ cả nh&agrave; n&oacute;i m&igrave;nh kh&ocirc;ng chăm được con, em đ&agrave;nh cho b&eacute; ăn dặm sớm hơn b&igrave;nh thường.</p>\r\n\r\n<p>rộm v&iacute;a con em ăn rất tốt, cứ c&aacute;ch 1 đến 2 tiếng l&agrave; b&eacute; lại đ&ograve;i ăn một lần. Em xem kẽ sữa, bột v&agrave; hoa quả ăn dặm cho phong ph&uacute; thực đơn của con. Một ng&agrave;y b&eacute; c&oacute; thể ăn được đến 4 bữa sữa, 2 bữa bột v&agrave; nhiều bữa hoa quả lặt vặt kh&aacute;c. Theo đ&oacute;, mỗi cữ b&uacute; b&eacute; ăn được 150ml sữa, c&ograve;n bột th&igrave; cũng ăn được một b&aacute;t con đầy. Vậy m&agrave; ăn bao nhi&ecirc;u em cũng kh&ocirc;ng thấy con l&ecirc;n c&acirc;n tẹo n&agrave;o. Được 5 th&aacute;ng rưỡi, b&eacute; chỉ nặng chưa tới 7 c&acirc;n. Em căng thẳng lắm v&igrave; thỉnh thoảng bố mẹ chồng đến thăm ch&aacute;u tuy kh&ocirc;ng n&oacute;i ra nhưng lại tỏ &yacute; x&oacute;t xa v&igrave; ch&aacute;u kh&ocirc;ng bụ bẫm. Em chỉ lo con bị c&ograve;i xương n&ecirc;n kh&ocirc;ng tăng c&acirc;n được.</p>\r\n\r\n<p>H&ocirc;m trước, em ph&aacute;t hiện lợi dưới của con sưng đỏ v&agrave; nh&uacute; l&ecirc;n 2 c&aacute;i răng sữa b&eacute; x&iacute;u. Em ngạc nhi&ecirc;n v&igrave; mới chưa đầy 6 th&aacute;ng m&agrave; con đ&atilde; mọc được răng. Th&ocirc;ng thường, em để &yacute; thấy trẻ m&atilde;i đến 7 hay 8 th&aacute;ng mới đến l&uacute;c răng bắt đầu mọc. Đ&uacute;ng h&ocirc;m b&agrave; nội b&eacute; sang chơi, b&agrave; nh&igrave;n răng ch&aacute;u v&agrave; k&ecirc;u ca rằng mọc răng sớm thế n&agrave;y sao m&agrave; bụ được, con em sẽ c&ograve;i m&atilde;i th&ocirc;i.</p>\r\n\r\n<p>Nghe vậy m&agrave; em thấy buồn qu&aacute;. Chẳng nhẽ n&agrave;o trẻ mọc răng sớm th&igrave; nhất định phải c&ograve;i? Đ&atilde; c&oacute; chị em n&agrave;o c&oacute; con giống trường hợp của em chưa? Nh&igrave;n b&eacute; cứ t&iacute; hin trong khi c&aacute;c bạn kh&aacute;c bụ bẫm, em thấy sốt ruột lắm&hellip;</p>\r\n', '2c239251c4a128995a6d083068af1cfb.jpg', '1402385917');
INSERT INTO `news` VALUES ('17', 'Bé mọc răng sớm ăn mấy cũng còi? ', 'Be-moc-rang-som-an-may-cung-coi-417', '2', 'Từ khi sinh con, lại là con trai đầu lòng và cháu độc đinh của nhà nội, em luôn được nhà chồng chiều chuộng hết mức. Mọi công việc nội trợ gia đình đều giao tất tật cho người giúp việc, em chỉ việc chăm con khỏe mạnh. Những tưởng như vậy là đã sung sướng, nhưng khổ nỗi, việc chăm con em cũng làm không đến nơi đến chốn.\r\n\r\nEm cho con bú mẹ hoàn toàn, theo lý thuyết, đáng lẽ bé nhà em phải bụ bẫm mới đúng. Vậy mà đến gần 5 tháng, trông con vẫn còi còi và bé hơn các bé cùng tháng nhiều, tay chân cũng không chắc bằng các bạn. Vừa lo con không đủ chất, lại vừa sợ cả nhà nói mình không chăm được con, em đành cho bé ăn dặm sớm hơn bình thường.', '<p>Từ khi sinh con, lại l&agrave; con trai đầu l&ograve;ng v&agrave; ch&aacute;u độc đinh của nh&agrave; nội, em lu&ocirc;n được nh&agrave; chồng chiều chuộng hết mức. Mọi c&ocirc;ng việc nội trợ gia đ&igrave;nh đều giao tất tật cho người gi&uacute;p việc, em chỉ việc chăm con khỏe mạnh. Những tưởng như vậy l&agrave; đ&atilde; sung sướng, nhưng khổ nỗi, việc chăm con em cũng l&agrave;m kh&ocirc;ng đến nơi đến chốn.</p>\r\n\r\n<p>Em cho con b&uacute; mẹ ho&agrave;n to&agrave;n, theo l&yacute; thuyết, đ&aacute;ng lẽ b&eacute; nh&agrave; em phải bụ bẫm mới đ&uacute;ng. Vậy m&agrave; đến gần 5 th&aacute;ng, tr&ocirc;ng con vẫn c&ograve;i c&ograve;i v&agrave; b&eacute; hơn c&aacute;c b&eacute; c&ugrave;ng th&aacute;ng nhiều, tay ch&acirc;n cũng kh&ocirc;ng chắc bằng c&aacute;c bạn. Vừa lo con kh&ocirc;ng đủ chất, lại vừa sợ cả nh&agrave; n&oacute;i m&igrave;nh kh&ocirc;ng chăm được con, em đ&agrave;nh cho b&eacute; ăn dặm sớm hơn b&igrave;nh thường.</p>\r\n\r\n<p>Trộm v&iacute;a con em ăn rất tốt, cứ c&aacute;ch 1 đến 2 tiếng l&agrave; b&eacute; lại đ&ograve;i ăn một lần. Em xem kẽ sữa, bột v&agrave; hoa quả ăn dặm cho phong ph&uacute; thực đơn của con. Một ng&agrave;y b&eacute; c&oacute; thể ăn được đến 4 bữa sữa, 2 bữa bột v&agrave; nhiều bữa hoa quả lặt vặt kh&aacute;c. Theo đ&oacute;, mỗi cữ b&uacute; b&eacute; ăn được 150ml sữa, c&ograve;n bột th&igrave; cũng ăn được một b&aacute;t con đầy. Vậy m&agrave; ăn bao nhi&ecirc;u em cũng kh&ocirc;ng thấy con l&ecirc;n c&acirc;n tẹo n&agrave;o. Được 5 th&aacute;ng rưỡi, b&eacute; chỉ nặng chưa tới 7 c&acirc;n. Em căng thẳng lắm v&igrave; thỉnh thoảng bố mẹ chồng đến thăm ch&aacute;u tuy kh&ocirc;ng n&oacute;i ra nhưng lại tỏ &yacute; x&oacute;t xa v&igrave; ch&aacute;u kh&ocirc;ng bụ bẫm. Em chỉ lo con bị c&ograve;i xương n&ecirc;n kh&ocirc;ng tăng c&acirc;n được.</p>\r\n\r\n<p>H&ocirc;m trước, em ph&aacute;t hiện lợi dưới của con sưng đỏ v&agrave; nh&uacute; l&ecirc;n 2 c&aacute;i răng sữa b&eacute; x&iacute;u. Em ngạc nhi&ecirc;n v&igrave; mới chưa đầy 6 th&aacute;ng m&agrave; con đ&atilde; mọc được răng. Th&ocirc;ng thường, em để &yacute; thấy trẻ m&atilde;i đến 7 hay 8 th&aacute;ng mới đến l&uacute;c răng bắt đầu mọc. Đ&uacute;ng h&ocirc;m b&agrave; nội b&eacute; sang chơi, b&agrave; nh&igrave;n răng ch&aacute;u v&agrave; k&ecirc;u ca rằng mọc răng sớm thế n&agrave;y sao m&agrave; bụ được, con em sẽ c&ograve;i m&atilde;i th&ocirc;i.</p>\r\n\r\n<p>Nghe vậy m&agrave; em thấy buồn qu&aacute;. Chẳng nhẽ n&agrave;o trẻ mọc răng sớm th&igrave; nhất định phải c&ograve;i? Đ&atilde; c&oacute; chị em n&agrave;o c&oacute; con giống trường hợp của em chưa? Nh&igrave;n b&eacute; cứ t&iacute; hin trong khi c&aacute;c bạn kh&aacute;c bụ bẫm, em thấy sốt ruột lắm&hellip;</p>\r\n', '5d204e15b910f072fff6d5fa3d34d748.jpg', '1402386083');
INSERT INTO `news` VALUES ('19', ' Cập nhật style tóc xinh cho bé gái ngày hè Cập nhật style tóc xinh cho bé gái ngày hè ', 'Cap-nhat-style-toc-xinh-cho-be-gai-ngay-he-Cap-nhat-style-toc-xinh-cho-be-gai-ngay-he', '2', 'Nếu mẹ đang có một cô con gái xinh xắn, chắc hẳn bé sẽ rất thích mẹ làm điệu cho mình bằng những kiểu tóc độc đáo khác nhau để diện đi chơi hè. Bên cạnh những kiểu tóc truyền thống như mái bằng đầu vuông hay tóc đuôi ngựa, mẹ hãy thử tham khảo một số biến tấu thú vị sau để giúp cô công chúa nhỏ của mình ngày càng xinh đẹp và đáng yêu hơn.', '<p>Nếu mẹ đang c&oacute; một c&ocirc; con g&aacute;i xinh xắn, chắc hẳn b&eacute; sẽ rất th&iacute;ch mẹ l&agrave;m điệu cho m&igrave;nh bằng những kiểu t&oacute;c độc đ&aacute;o kh&aacute;c nhau để diện đi chơi h&egrave;. B&ecirc;n cạnh những kiểu t&oacute;c truyền thống như m&aacute;i bằng đầu vu&ocirc;ng hay t&oacute;c đu&ocirc;i ngựa, mẹ h&atilde;y thử tham khảo một số biến tấu th&uacute; vị sau để gi&uacute;p c&ocirc; c&ocirc;ng ch&uacute;a nhỏ của m&igrave;nh ng&agrave;y c&agrave;ng xinh đẹp v&agrave; đ&aacute;ng y&ecirc;u hơn.</p>\r\n\r\n<p><strong>1. T&oacute;c tết đu&ocirc;i sam đ&iacute;nh hạt cườm</strong></p>\r\n\r\n<p>Nếu mẹ muốn con g&aacute;i tr&ocirc;ng gọn g&agrave;ng m&agrave; vẫn xinh đẹp v&ocirc; tư vui đ&ugrave;a th&igrave; t&oacute;c tết l&agrave; một gợi &yacute; kh&ocirc;ng tồi. Tuy nhi&ecirc;n kiểu t&oacute;c tết 3 lọn đơn giản m&agrave; mẹ vẫn tết cho b&eacute; h&agrave;ng ng&agrave;y th&igrave; kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng n&oacute;i. Tr&ecirc;n thực tế, mẹ chỉ cần biến tấu bằng c&aacute;ch tết t&oacute;c đu&ocirc;i sam l&agrave; đ&atilde; tạo ra một sự kh&aacute;c biệt lớn. Cộng th&ecirc;m một ch&uacute;t hạt cườm đ&iacute;nh v&agrave;o mỗi lọn t&oacute;c, b&eacute; tr&ocirc;ng sẽ thật kh&aacute;c biệt v&agrave; dịu d&agrave;ng hơn gấp bội. C&aacute;ch đ&iacute;nh hạt cườm rất đơn giản, mẹ c&oacute; thể cố định hạt v&agrave; đầu mỗi chiếc ghim t&oacute;c nhỏ v&agrave; c&agrave;i v&agrave;o cho con.</p>\r\n\r\n<p><strong>2. T&oacute;c buộc hai b&ecirc;n với m&aacute;i lệch</strong></p>\r\n\r\n<p>C&ocirc; b&eacute; tinh nghịch của mẹ sẽ rất xinh đẹp với 2 lọn t&oacute;c được buộc cao sang hai b&ecirc;n. Kiểu t&oacute;c n&agrave;y sẽ c&agrave;ng đ&aacute;ng y&ecirc;u hơn nếu mẹ chải m&aacute;i lệch v&agrave; cắt một ch&uacute;t m&aacute;i tạo kiểu cho b&eacute;. Kh&ocirc;ng cần qu&aacute; chải chuốt v&agrave; kĩ c&agrave;ng, một ch&uacute;t t&oacute;c rối sẽ c&agrave;ng l&agrave;m b&eacute; đ&aacute;ng y&ecirc;u v&agrave; tự nhi&ecirc;n hơn.</p>\r\n\r\n<p><strong>3. Biến t&oacute;c t&oacute;c ngắn c&ugrave;ng bờm</strong></p>\r\n\r\n<p>D&ugrave; kh&ocirc;ng sở hữu m&aacute;i t&oacute;c d&agrave;i, nhưng những c&ocirc; b&eacute; với t&oacute;c ngắn sẽ rất đ&aacute;ng y&ecirc;u c&ugrave;ng một chiếc bờm xinh xắn. L&agrave; một phụ kiện kh&ocirc;ng thể thiếu cho c&aacute;c b&eacute; g&aacute;i trong h&egrave;, những chiếc bờm kh&ocirc;ng chỉ l&agrave;m nổi bật kiểu t&oacute;c của c&aacute;c con m&agrave; c&ograve;n gi&uacute;p ngăn chặn những cọng t&oacute;c m&aacute;i &ldquo;kh&oacute; bảo&rdquo; x&ograve;a v&agrave;o mắt, v&agrave;o chiếc tr&aacute;n đẫm mồ h&ocirc;i của con trong ng&agrave;y n&oacute;ng.</p>\r\n\r\n<p><strong>4. T&oacute;c b&uacute;i s&agrave;nh điệu</strong></p>\r\n\r\n<p>Mẹ đừng nghĩ b&eacute; c&ograve;n nhỏ kh&ocirc;ng thể để kiểu t&oacute;c b&uacute;i cao &ldquo;gi&agrave; dặn&rdquo;. Chỉ cần kh&eacute;o l&eacute;o kết hợp m&aacute;i t&oacute;c c&ugrave;ng những phụ kiện thời trang cho con, b&eacute; đ&atilde; nhanh ch&oacute;ng trở th&agrave;nh một qu&yacute; c&ocirc; s&agrave;nh điệu c&ugrave;ng mẹ đi mua sắm. Chắc chắn mẹ v&agrave; b&eacute; sẽ l&agrave; t&acirc;m điểm ngước nh&igrave;n của trung t&acirc;m thương mại h&ocirc;m đ&oacute;. Kiểu t&oacute;c n&agrave;y kh&ocirc;ng chỉ mang t&iacute;nh thời trang m&agrave; c&ograve;n dễ thực hiện v&agrave; lu&ocirc;n giữ cho khu&ocirc;n mặt b&eacute; gọn g&agrave;ng. Mẹ c&oacute; thể thực hiện kiểu t&oacute;c n&agrave;y ngay cả khi t&oacute;c b&eacute; d&agrave;y hay mỏng.</p>\r\n\r\n<p><strong>5. T&oacute;c d&agrave;i tết xương c&aacute; hai b&ecirc;n</strong></p>\r\n\r\n<p>Đối với những ng&agrave;y h&egrave; với thời tiết m&aacute;t mẻ một ch&uacute;t, mẹ c&oacute; thể l&agrave;m cho b&eacute; một kiểu t&oacute;c bồng bềnh đẹp tuyệt nhưng cũng kh&ocirc;ng qu&aacute; phức tạp. Mẹ c&oacute; thể chia t&oacute;c b&eacute; đều hai b&ecirc;n v&agrave; tết xương c&aacute;. Mẹ h&atilde;y tết lỏng tay một ch&uacute;t để tạo độ phồng cho t&oacute;c v&agrave; biến tấu bằng c&aacute;ch buộc t&uacute;m ch&acirc;n lọn t&oacute;c một c&aacute;ch tinh nghịch. Với kiểu t&oacute;c n&agrave;y, mẹ chừa ra một ch&uacute;t m&aacute;i v&agrave; chải phồng, lệch sang một b&ecirc;n sẽ l&agrave;m b&eacute; th&ecirc;m dịu d&agrave;ng v&agrave; nữ t&iacute;nh. Chắc chắn mọi người sẽ trầm trồ trước độ kh&eacute;o tay của mẹ khi nh&igrave;n thấy m&aacute;i t&oacute;c của b&eacute;.</p>\r\n\r\n<p><strong>6. T&oacute;c ngắn tết hai b&ecirc;n</strong></p>\r\n\r\n<p>M&aacute;i t&oacute;c ngắn của b&eacute; sẽ dễ d&agrave;ng biến tấu với kiểu t&oacute;c tết hai b&ecirc;n đơn giản nhưng lại v&ocirc; c&ugrave;ng đ&aacute;ng y&ecirc;u. Một ch&uacute;t m&aacute;i lệch giắt tai nhẹ nh&agrave;ng sẽ l&agrave;m con điệu đ&agrave; hơn rất nhiều. Đ&acirc;y l&agrave; kiểu t&oacute;c m&ugrave;a h&egrave; v&ocirc; c&ugrave;ng ph&ugrave; hợp với b&eacute; trong mọi thời tiết.</p>\r\n\r\n<p>C&oacute; lẽ kh&ocirc;ng b&eacute; n&agrave;o l&agrave; kh&ocirc;ng h&aacute;o hứng được thử một trong c&aacute;c kiểu đầu đ&aacute;ng y&ecirc;u n&agrave;y trong những ng&agrave;y h&egrave; sắp tới. Mẹ h&atilde;y thử xem sao!</p>\r\n', '880486f524bb4af4a404c4b54137e739.jpg', '1402386511');
INSERT INTO `news` VALUES ('20', 'Khoai sọ, \'thuốc bổ\' mùa hè cho con ', 'Khoai-so-thuoc-bo-mua-he-cho-con', '2', 'Rất nhiều mẹ luôn trong tình trạng “loay hoay” khi soạn thực đơn ăn dặm cho bé. Làm thế nào để món ăn đa dạng phong phú mà vẫn đủ dinh dưỡng là thắc mắc của không ít chị em. Thay vì loanh quanh với cà rốt, củ cải, bí đỏ, hay khoai tây, mẹ hãy thử chọn một nguyên liệu hoàn toàn mới mà lại “siêu” bổ dưỡng cho bé : khoai sọ. Không chỉ là một nguyên liệu để nấu món canh cua khoai sọ yêu thích của cả gia đình, khoai sọ cũng đồng thời là một món ăn dặm chứa đầy dinh dưỡng mà bé sẽ thích mê.', '<p>Rất nhiều mẹ lu&ocirc;n trong t&igrave;nh trạng &ldquo;loay hoay&rdquo; khi soạn thực đơn <a href=\"http://eva.vn/an-dam-c66e445.html\">ăn dặm cho b&eacute;</a>. L&agrave;m thế n&agrave;o để m&oacute;n ăn đa dạng phong ph&uacute; m&agrave; vẫn đủ dinh dưỡng l&agrave; thắc mắc của kh&ocirc;ng &iacute;t chị em. Thay v&igrave; loanh quanh với c&agrave; rốt, củ cải, b&iacute; đỏ, hay khoai t&acirc;y, mẹ h&atilde;y thử chọn một nguy&ecirc;n liệu ho&agrave;n to&agrave;n mới m&agrave; lại &ldquo;si&ecirc;u&rdquo; bổ dưỡng cho b&eacute; : khoai sọ. Kh&ocirc;ng chỉ l&agrave; một nguy&ecirc;n liệu để nấu m&oacute;n canh cua khoai sọ y&ecirc;u th&iacute;ch của cả gia đ&igrave;nh, khoai sọ cũng đồng thời l&agrave; một m&oacute;n ăn dặm chứa đầy dinh dưỡng m&agrave; b&eacute; sẽ th&iacute;ch m&ecirc;.</p>\r\n\r\n<p><strong>Th&agrave;nh phần dinh dưỡng của khoai sọ</strong></p>\r\n\r\n<p>D&ugrave; vẫn nấu khoai sọ h&agrave;ng ng&agrave;y, nhưng c&oacute; nhiều mẹ kh&ocirc;ng nghĩ rằng trong thực phẩm n&agrave;y lại chứa rất nhiều chất dinh dưỡng. Trong một củ khoai b&eacute; tẹo lại bao h&agrave;m c&aacute;c chất tinh bột, protid, lipid, galactose, Ca, P, F; c&aacute;c vitamin A, B, C v&agrave; nhiều axit amin cần thiết cho cơ thể.</p>\r\n\r\n<p><strong>T&aacute;c dụng kh&oacute; tin của khoai sọ</strong></p>\r\n\r\n<p>Khoai sọ c&oacute; rẩt nhiều t&aacute;c dụng tốt cho cơ thể mẹ v&agrave; b&eacute;. Một trong những c&ocirc;ng dụng đối với trẻ m&agrave; nhiều mẹ sẽ rất quan t&acirc;m l&agrave; khả năng gi&uacute;p nhuận tr&agrave;ng v&agrave; chống t&aacute;o b&oacute;n của loại thực phẩm n&agrave;y. Nếu mẹ đang đau đầu v&igrave; con đ&atilde; v&agrave;i ng&agrave;y &ldquo;kh&ocirc;ng thấy g&igrave;&rdquo; th&igrave; khoai sọ đ&uacute;ng l&agrave; m&oacute;n mẹ cần.</p>\r\n\r\n<p>Ngo&agrave;i ra, khoai sọ c&ograve;n c&oacute; nhiều t&aacute;c dụng kh&aacute;c như chống suy nhược cơ thể, ti&ecirc;u kh&aacute;t, chữa mệt mỏi, k&eacute;m ăn, kiết lỵ&hellip; Nếu mẹ đang lo lắng v&igrave; b&eacute; c&oacute; một trong c&aacute;c triệu chứng tr&ecirc;n th&igrave; khoai sọ đ&uacute;ng l&agrave; một giải ph&aacute;p tuyệt vời.</p>\r\n\r\n<p><strong>B&eacute; ăn được khoai sọ khi n&agrave;o</strong></p>\r\n\r\n<p>B&eacute; từ 6 th&aacute;ng v&agrave; bắt đầu tập ăn dặm đ&atilde; c&oacute; thể ăn được khoai sọ nếu mẹ biết c&aacute;ch chế biến hợp l&yacute;. Tất nhi&ecirc;n, lượng khoai mẹ cho b&eacute; ăn chỉ l&agrave; một &iacute;t v&agrave; tăng dần theo độ tuổi của con.</p>\r\n\r\n<p><strong>C&aacute;c m&oacute;n ăn dặm từ khoai sọ cho b&eacute;</strong></p>\r\n\r\n<p>Mẹ h&atilde;y tham khảo một số gợi &yacute; về c&aacute;c m&oacute;n ăn dặm từ loại thực phẩm bổ dưỡng n&agrave;y cho b&eacute; nh&agrave; m&igrave;nh theo từng th&aacute;ng ph&ugrave; hợp sau đ&acirc;y:</p>\r\n\r\n<p><em>D&agrave;nh cho b&eacute; từ 6 th&aacute;ng: Ch&aacute;o khoai sọ</em></p>\r\n\r\n<p>Nguy&ecirc;n liệu: Khoai sọ, nước luộc rau củ hoặc nước dashi, gạo nấu ch&aacute;o, nước.</p>\r\n\r\n<p>C&aacute;ch l&agrave;m:<br />\r\nKhi b&eacute; mới bẳt đầu ăn dặm, mẹ nấu ch&aacute;o theo tỉ lệ 1 gạo, 10 nước v&agrave; tăng dần theo th&aacute;ng tuổi của con. Ch&aacute;o nấu ở mức độ lo&atilde;ng ph&ugrave; hợp với b&eacute;.<br />\r\nKhoai sọ mẹ ninh nhừ, sau đ&oacute; dằm n&aacute;t c&ugrave;ng với nước ninh hoặc nước rau củ, nước dashi theo sở th&iacute;ch của mẹ. Mẹ n&ecirc;n l&agrave;m ở độ đặc vừa phải để tr&aacute;nh b&eacute; bị nghẹn, kh&oacute; nuốt.<br />\r\nCuối c&ugrave;ng, mẹ trộn th&agrave;nh phẩm khoai đ&atilde; chế biến tr&ecirc;n v&agrave;o ch&aacute;o cho b&eacute; ăn. Như vậy l&agrave; con đ&atilde; c&oacute; một b&aacute;t ch&aacute;o khoai ngon l&agrave;nh rồi.</p>\r\n\r\n<p><em>D&agrave;nh cho b&eacute; từ 7-8 th&aacute;ng: Khoai sọ nấu rau cải</em></p>\r\n\r\n<p>Nguy&ecirc;n liệu: 3 th&igrave;a khoai sọ, 1 th&igrave;a rau cải, 5 -6 th&igrave;a nước dashi/nước luộc rau củ.</p>\r\n\r\n<p>C&aacute;ch l&agrave;m:<br />\r\nKhoai sọ mẹ chế biến như tr&ecirc;n, luộc kĩ rồi dằm n&aacute;t<br />\r\nRau cải lọc lấy phần l&aacute;, sau đ&oacute; luộc ch&iacute;n v&agrave; băm nhỏ<br />\r\nCho khoai sọ, rau đ&atilde; chế biến ở tr&ecirc;n v&agrave;o nồi v&agrave; đun lại cho s&ocirc;i l&agrave; được.</p>\r\n\r\n<p>Đối với c&aacute;c b&eacute; lớn hơn, mẹ c&oacute; thể bổ sung th&ecirc;m c&aacute;c loại thịt kh&aacute;c v&agrave;o ch&aacute;o để m&oacute;n ăn th&ecirc;m hấp dẫn.</p>\r\n', '785e76b4229a9f335f7ed79d70aad179.jpg', '1402386758');
INSERT INTO `news` VALUES ('21', 'Cho trẻ đi bơi, cẩn thận rước bệnh', 'Cho-tre-di-boi-can-than-ruoc-benh', '2', 'Mùa hè đến là khi các mẹ rục rịch cho bé tham gia các hoạt động thể thao hay sinh hoạt ngoại khóa để bù vào thời gian nghỉ hè của bé. Từ rất nhiều năm nay, bơi luôn là môn thể thao được các mẹ ưu tiên cho bé tham gia nhất bởi nó không chỉ giúp bé giải nhiệt trong thời tiết nắng nóng mà còn giúp con cao lớn và khỏe mạnh. Tuy nhiên, nếu mẹ không cẩn thận trong việc chọn lựa bể bơi và sắp xếp thời gian bơi hợp lí, đi bơi cũng không hoàn toàn tốt như mẹ tưởng.', '<p><strong>Nếu kh&ocirc;ng cẩn thận chọn lựa bể bơi v&agrave; sắp xếp thời gian bơi hợp l&iacute;, cho b&eacute; đi bơi kh&ocirc;ng ho&agrave;n to&agrave;n tốt như mẹ tưởng.</strong></p>\r\n\r\n<p>M&ugrave;a h&egrave; đến l&agrave; khi c&aacute;c mẹ rục rịch cho b&eacute; tham gia c&aacute;c hoạt động thể thao hay sinh hoạt ngoại kh&oacute;a để b&ugrave; v&agrave;o thời gian nghỉ h&egrave; của b&eacute;. Từ rất nhiều năm nay, bơi lu&ocirc;n l&agrave; m&ocirc;n thể thao được c&aacute;c mẹ ưu ti&ecirc;n cho b&eacute; tham gia nhất bởi n&oacute; kh&ocirc;ng chỉ gi&uacute;p b&eacute; giải nhiệt trong thời tiết nắng n&oacute;ng m&agrave; c&ograve;n gi&uacute;p con cao lớn v&agrave; khỏe mạnh. Tuy nhi&ecirc;n, nếu mẹ kh&ocirc;ng cẩn thận trong việc chọn lựa bể bơi v&agrave; sắp xếp thời gian bơi hợp l&iacute;, đi bơi cũng kh&ocirc;ng ho&agrave;n to&agrave;n tốt như mẹ tưởng.</p>\r\n\r\n<p><strong>&ldquo;Sốc&rdquo; v&igrave; bể bơi rắc đầy h&oacute;a chất</strong></p>\r\n\r\n<p>D&ugrave; rất muốn con đi học bơi, nhưng c&oacute; một số mẹ vẫn băn khoăn kh&ocirc;ng biết bể bơi m&igrave;nh chọn c&oacute; đảm bảo an to&agrave;n cho b&eacute; hay kh&ocirc;ng. Lo lắng của c&aacute;c mẹ c&agrave;ng tăng khi mới đ&acirc;y xuất hiện vụ việc rắc h&oacute;a chất l&agrave;m trong nước bể bơi xảy ra tr&ecirc;n địa b&agrave;n H&agrave; Nội. Theo đ&oacute;, v&agrave;o chiều ng&agrave;y 20/5/2014, một cư d&acirc;n sống gần bể bơi tại Trung t&acirc;m thể dục thể thao Quận Cầu Giấy đ&atilde; quay được 1 đoạn clip cảnh nh&acirc;n vi&ecirc;n hồ bơi rắc một loại h&oacute;a chất kh&ocirc;ng r&otilde; nguồn gốc v&agrave;o bể. Một thời gian ngắn sau đ&oacute;, nước trong bể đang đục ngầu, chứa đầy rong r&ecirc;u đ&atilde; chuyển th&agrave;nh trong v&agrave; xanh như được thay nước mới. Được biết, số nước n&agrave;y đ&atilde; chứa trong bể 1 năm nay.</p>\r\n\r\n<p>Xuất hiện chưa đầy 2 ng&agrave;y, nhưng đoạn clip đ&atilde; l&agrave;m kh&ocirc;ng &iacute;t mẹ hoang mang, nhất l&agrave; những mẹ đang c&oacute; &yacute; định cho b&eacute; đi bơi h&egrave; n&agrave;y. Đa số c&aacute;c mẹ đều tỏ &yacute; lo lắng kh&ocirc;ng hiểu con em m&igrave;nh sẽ thế n&agrave;o nếu bơi trong một bể nước như vậy.</p>\r\n\r\n<p>R&uacute;t kinh nghiệm từ vụ việc n&agrave;y, c&aacute;c mẹ n&ecirc;n t&igrave;m hiểu kĩ những bể bơi m&agrave; mẹ định đăng k&iacute; cho b&eacute; học. Nếu cần thiết, chỉ n&ecirc;n cho b&eacute; bơi ở c&aacute;c bể uy t&iacute;n v&agrave; đảm bảo chất lượng, c&oacute; thể gi&aacute; th&agrave;nh cao một ch&uacute;t nhưng lại an to&agrave;n cho b&eacute;.</p>\r\n\r\n<p><strong>Nguy cơ hen suyễn từ chất clo l&agrave;m sạch bể</strong></p>\r\n\r\n<p>Hầu như to&agrave;n bộ bể bơi hiện nay đều khử tr&ugrave;ng bằng chất clo. Thế nhưng, c&aacute;c nh&agrave; khoa học Bỉ đ&atilde; ph&aacute;t hiện ra rằng chất clo n&agrave;y sẽ phản ứng với mồ h&ocirc;i v&agrave; nước tiểu của người c&oacute; trong nước bể bơi. Phản ứng ho&aacute; học sẽ diễn ra v&agrave; tạo ra một số kh&iacute; c&oacute; t&aacute;c động rất xấu đến phổi người. Loại kh&iacute; n&agrave;y ch&iacute;nh l&agrave; nguy&ecirc;n nh&acirc;n dẫn đến dị ứng đường h&ocirc; hấp ở trẻ em v&agrave; c&oacute; thể l&agrave; dấu hiệu đầu ti&ecirc;n của bệnh hen. Kh&ocirc;ng chỉ vậy, loại h&oacute;a chất n&agrave;y cũng đồng thời l&agrave; nguy&ecirc;n nh&acirc;n g&acirc;y kh&ocirc; da, rụng t&oacute;c.</p>\r\n\r\n<p>Tất nhi&ecirc;n nếu dựa tr&ecirc;n một nghi&ecirc;n cứu m&agrave; kh&ocirc;ng b&eacute; cho đi bơi th&igrave; rất thiệt th&ograve;i cho trẻ. Tuy nhi&ecirc;n, để đảm bảo an to&agrave;n hơn cho con, c&aacute;c mẹ c&oacute; thể y&ecirc;u cầu b&eacute; kh&ocirc;ng ở dưới nước qu&aacute; l&acirc;u cũng như tắm rửa thật sạch ngay sau khi ra khỏi bể bơi.</p>\r\n\r\n<p><strong>V&agrave; c&aacute;c nguy hiểm tiềm ẩn kh&aacute;c khi b&eacute; đi bơi</strong></p>\r\n\r\n<p>Ngo&agrave;i nỗi lo h&oacute;a chất, việc chọn bể bơi kh&ocirc;ng đảm bảo cũng c&oacute; thể tiềm ẩn nhiều nguy cơ g&acirc;y bệnh cho b&eacute;. B&eacute; rất dễ bị l&acirc;y c&aacute;c bệnh truyền nhiễm như đau mắt đỏ, bệnh da liễu&hellip; nếu c&oacute; người đang mắc bệnh bơi c&ugrave;ng bể.</p>\r\n\r\n<p>Hơn nữa, mẹ n&ecirc;n lưu &yacute; kh&ocirc;ng để b&eacute; bơi khi trời nắng n&oacute;ng. Khi ấy, nhiệt độ cơ thể b&eacute; đang cao, mồ h&ocirc;i ra nhiều v&agrave; gặp nước bể sẽ rất dễ bị cảm đột ngột. Thay v&agrave;o đ&oacute;, mẹ n&ecirc;n cho b&eacute; bơi v&agrave;o thời điểm đầu giờ s&aacute;ng v&agrave; cuối giờ chiều, sẽ rất c&oacute; lợi cho sức khỏe của con. B&ecirc;n cạnh đ&oacute;, trước khi trẻ bơi phải dội nước cho trẻ ướt hết người mới xuống bể. Đồng thời, mẹ h&atilde;y c&ugrave;ng b&eacute; khởi động c&aacute;c khớp cổ ch&acirc;n tay gi&uacute;p cơ thể mềm dẻo, tr&aacute;nh việc b&eacute; bị chuột r&uacute;t khi đang bơi.</p>\r\n', '1b6113e1ce09e2b2b83f149005219a5c.jpg', '1402386961');
INSERT INTO `news` VALUES ('22', 'Cách dùng điều hoà, quạt để con không ốm ', 'Cach-dung-dieu-hoa-quat-de-con-khong-om', '2', 'Mùa nóng đến là khi các mẹ “nô nức” tìm mua các sản phẩm để làm mát cho bé. Nhưng khi đứng trước các lựa chọn, mẹ sẽ “hoa mắt chóng mặt” bởi quá nhiều dòng sản phẩm khác nhau và băn khoăn không biết con mình phù hợp với các thiết bị nào và các thiết bị nào cần “tránh xa”. Để mẹ có cái nhìn bao quát hơn về vấn đề này, hãy cùng điểm lại một số thiết bị làm mát tiêu biểu và ưu nhược điểm của nó với bé.', '<p>M&ugrave;a n&oacute;ng đến l&agrave; khi c&aacute;c mẹ &ldquo;n&ocirc; nức&rdquo; t&igrave;m mua c&aacute;c sản phẩm để l&agrave;m m&aacute;t cho b&eacute;. Nhưng khi đứng trước c&aacute;c lựa chọn, mẹ sẽ &ldquo;hoa mắt ch&oacute;ng mặt&rdquo; bởi qu&aacute; nhiều d&ograve;ng sản phẩm kh&aacute;c nhau v&agrave; băn khoăn kh&ocirc;ng biết <a href=\"http://eva.vn/nuoi-con-c13.html\">con</a> m&igrave;nh ph&ugrave; hợp với c&aacute;c thiết bị n&agrave;o v&agrave; c&aacute;c thiết bị n&agrave;o cần &ldquo;tr&aacute;nh xa&rdquo;. Để mẹ c&oacute; c&aacute;i nh&igrave;n bao qu&aacute;t hơn về vấn đề n&agrave;y, h&atilde;y c&ugrave;ng điểm lại một số thiết bị l&agrave;m m&aacute;t ti&ecirc;u biểu v&agrave; ưu nhược điểm của n&oacute; với b&eacute;.</p>\r\n\r\n<p><strong>Điều h&ograve;a nhiệt độ</strong></p>\r\n\r\n<p>Điều h&ograve;a l&agrave; một thiết bị phổ biến trong tiết trời nắng n&oacute;ng như hiện nay. Tuy nhi&ecirc;n việc sử dụng thiết bị n&agrave;y cho b&eacute; vẫn khiến nhiều mẹ e ngại bởi một số t&aacute;c dụng phụ kh&ocirc;ng mong muốn dễ ảnh hưởng tới b&eacute; như kh&ocirc; da, cảm lạnh, nghẹt mũi, sốt&hellip;</p>\r\n\r\n<p>Theo babycenter, nếu biết sử dụng đ&uacute;ng c&aacute;ch, điều h&ograve;a thậm ch&iacute; lại an to&agrave;n cho trẻ bởi n&oacute; ổn định nhiệt độ v&agrave; gi&uacute;p b&eacute; tr&aacute;nh khỏi m&ocirc;i trường n&oacute;ng nực, ẩm thấp. Tuy nhi&ecirc;n, mẹ phải để nhiệt độ ph&ugrave; hợp ở mức 28-29 độ v&agrave; c&oacute; c&aacute;ch giữ ẩm tốt cho da b&eacute;. Ngo&agrave;i ra, mẹ n&ecirc;n cho b&eacute; nằm ở những nơi kh&ocirc;ng c&oacute; gi&oacute; điều h&ograve;a thổi trực tiếp v&agrave; kh&ocirc;ng cho b&eacute; ra ngo&agrave;i n&oacute;ng đột ngột khi đang trong ph&ograve;ng lạnh. C&aacute;c mẹ c&oacute; thể tham khảo th&ecirc;m &yacute; kiến của b&aacute;c sĩ chuy&ecirc;n khoa về vấn đề n&agrave;y.</p>\r\n\r\n<p><strong>C&aacute;c loại quạt</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; thiết bị l&agrave;m m&aacute;t th&ocirc;ng dụng v&agrave; c&oacute; t&iacute;nh kinh tế cao nhất. Chắc kh&ocirc;ng mẹ n&agrave;o phủ nhận t&aacute;c dụng của quạt trong m&ugrave;a h&egrave;, nhất l&agrave; đối với trẻ. Theo th&ocirc;ng tin từ chuy&ecirc;n trang sức khỏe Webmd, sử dụng quạt c&oacute; thể giảm tới 72% nguy cơ đột tử ở trẻ sơ sinh bởi độ tho&aacute;ng m&aacute;t m&agrave; quạt mang lại. Sử dụng quạt trần trong nh&agrave; cũng gi&uacute;p kh&ocirc;ng kh&iacute; trở n&ecirc;n tho&aacute;ng m&aacute;t hơn so với quạt c&acirc;y, b&eacute; cũng sẽ cảm thấy dễ chịu hơn.</p>\r\n\r\n<p>Tuy phổ biến như vậy, nhưng c&aacute;c mẹ cũng cần lưu &yacute; khi d&ugrave;ng quạt cho b&eacute; trong m&ugrave;a n&oacute;ng. Điều quan trọng nhất l&agrave; kh&ocirc;ng n&ecirc;n để quạt qu&aacute; mạnh thổi trực tiếp v&agrave;o mặt b&eacute;. Nếu t&igrave;nh trạng n&agrave;y k&eacute;o d&agrave;i, b&eacute; c&oacute; thể sẽ bị cảm lạnh hoặc thậm ch&iacute; l&agrave; kh&oacute; thở.</p>\r\n\r\n<p><strong>Quạt phun sương/ quạt hơi nước</strong></p>\r\n\r\n<p>Đ&acirc;y l&agrave; một thiết bị l&agrave;m m&aacute;t mới được phổ biến trong v&agrave;i năm gần đ&acirc;y. Nếu nghe quảng c&aacute;o, hẳn nhiều mẹ sẽ rất th&iacute;ch th&uacute; bởi t&iacute;nh tiết kiệm v&agrave; c&ocirc;ng dụng m&agrave; loại quạt n&agrave;y mang lại. Nhưng tr&aacute;i với những g&igrave; mẹ mong đợi, đ&acirc;y lại l&agrave; một thiết bị c&oacute; hại với b&eacute;.</p>\r\n\r\n<p>Quạt hơi nước nếu d&ugrave;ng l&acirc;u, độ ẩm trong kh&ocirc;ng kh&iacute; tăng cao c&oacute; thể tạo điều kiện cho nấm mốc v&agrave;&nbsp; c&aacute;c loại vi khuẩn g&acirc;y hại cho sức khỏe c&oacute; cơ hội ph&aacute;t triển. Đặc biệt với c&aacute;c b&eacute;, do sức đề kh&aacute;ng c&ograve;n chưa cao, rất dễ mắc c&aacute;c bệnh tai mũi họng do kh&ocirc;ng kh&iacute; ẩm thấp. V&igrave; vậy d&ugrave; tiện dụng thế n&agrave;o th&igrave; mẹ cũng kh&ocirc;ng n&ecirc;n sử dụng thiết bị n&agrave;y với trẻ.</p>\r\n', '20ac5d0ce4eb517f76a3317cc3047722.jpg', '1402387069');
INSERT INTO `news` VALUES ('23', 'Đồ ăn dặm mẹ phải bỏ ngay khi mất điện ', 'Do-an-dam-me-phai-bo-ngay-khi-mat-dien', '2', 'Mùa nóng đến là lúc hầu hết mọi gia đình tăng cường sử dụng các thiết bị điện trong nhà, nhất là những nhà có trẻ nhỏ. Chính vì lượng điện tiêu thụ quá cao, tình trạng luân phiên cắt điện thường xuyên diễn ra trong hè khiến nhiều mẹ “lao đao” trong việc bảo quản đồ ăn cho bé. Có những món mẹ đã kì công chuẩn bị cho bé ăn ngày hôm sau, vừa bỏ vào tủ lạnh đã mất điện đến vài tiếng. Vậy là công sức của mẹ “đổ sông đổ bể”. Tuy nhiên rất nhiều mẹ tiếc công nên vẫn chờ có điện và lại “bảo quản” tiếp đồ ăn. Trên thực tế, có những thức ăn mẹ vẫn có thể để lại tủ lạnh sau khi mất điện nhưng có những món mẹ phải loại bỏ ngay lập tức. Mẹ hãy tham khảo những món không được tiếp tục sử dụng sau khi đã mất điện sau đây để đảm bảo an toàn cho các bữa ăn của bé:', '<p>M&ugrave;a n&oacute;ng đến l&agrave; l&uacute;c hầu hết mọi gia đ&igrave;nh tăng cường sử dụng c&aacute;c thiết bị điện trong nh&agrave;, nhất l&agrave; những nh&agrave; c&oacute; trẻ nhỏ. Ch&iacute;nh v&igrave; lượng điện ti&ecirc;u thụ qu&aacute; cao, t&igrave;nh trạng lu&acirc;n phi&ecirc;n cắt điện thường xuy&ecirc;n diễn ra trong h&egrave; khiến nhiều mẹ &ldquo;lao đao&rdquo; trong việc bảo quản đồ ăn cho b&eacute;. C&oacute; những m&oacute;n mẹ đ&atilde; k&igrave; c&ocirc;ng chuẩn bị cho b&eacute; ăn ng&agrave;y h&ocirc;m sau, vừa bỏ v&agrave;o tủ lạnh đ&atilde; mất điện đến v&agrave;i tiếng. Vậy l&agrave; c&ocirc;ng sức của mẹ &ldquo;đổ s&ocirc;ng đổ bể&rdquo;. Tuy nhi&ecirc;n rất nhiều mẹ tiếc c&ocirc;ng n&ecirc;n vẫn chờ c&oacute; điện v&agrave; lại &ldquo;bảo quản&rdquo; tiếp đồ ăn. Tr&ecirc;n thực tế, c&oacute; những thức ăn mẹ vẫn c&oacute; thể để lại tủ lạnh sau khi mất điện nhưng c&oacute; những m&oacute;n mẹ phải loại bỏ ngay lập tức. Mẹ h&atilde;y tham khảo những m&oacute;n kh&ocirc;ng được tiếp tục sử dụng sau khi đ&atilde; mất điện sau đ&acirc;y để đảm bảo an to&agrave;n cho c&aacute;c bữa ăn của b&eacute;:</p>\r\n\r\n<p><strong>Thịt gia s&uacute;c, gia cầm v&agrave; hải sản</strong></p>\r\n\r\n<p>Thịt b&ograve;, lợn,thịt gia cầm sống hay hải sản như c&aacute; hồi, ngao&hellip; sẽ c&oacute; nguy cơ hỏng ngay khi để qu&aacute; 2 tiếng ở nhiệt độ b&ecirc;n ngo&agrave;i. Danh s&aacute;ch n&agrave;y bao gồm cả đồ ăn thừa để tủ lạnh đ&atilde; qua chế biến, v&agrave; to&agrave;n bộ c&aacute;c m&oacute;n ăn li&ecirc;n quan đến thịt hoặc hải sản kh&aacute;c như salad c&aacute; ngừ, pizza v.v.</p>\r\n\r\n<p>Đối với c&aacute;c b&eacute; đang trong độ tuổi ăn dặm, nhiều mẹ c&oacute; th&oacute;i quen chế biến c&aacute;c m&oacute;n ăn mặn sẵn v&agrave; cho v&agrave;o tủ lạnh để nấu ch&aacute;o dần. C&aacute;c mẹ n&ecirc;n lưu &yacute; kĩ nếu chẳng may nh&agrave; mất điện th&igrave; mẹ cũng kh&ocirc;ng được &ldquo;tiếc rẻ&rdquo; m&agrave; nấu cho b&eacute; những đồ ăn n&agrave;y.</p>\r\n\r\n<p><strong>C&aacute;c sản phẩm từ sữa</strong></p>\r\n\r\n<p>Hẳn tủ lạnh của mẹ sẽ chất đầy những sản phẩm n&agrave;y khi nh&agrave; c&oacute; em b&eacute;. Tuy nhi&ecirc;n nếu mất điện, to&agrave;n bộ c&aacute;c sản phẩm từ sữa như sữa tươi, kem, sữa chua &hellip; đều kh&ocirc;ng thể sử dụng lại sau khi c&oacute; điện nữa. Ngay cả c&aacute;c t&uacute;i sữa mẹ đ&ocirc;ng lạnh cũng trở n&ecirc;n kh&ocirc;ng an to&agrave;n khi để trong tủ lạnh m&agrave; kh&ocirc;ng c&oacute; điện.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, bơ v&agrave; bơ thực vật lại nằm ngo&agrave;i danh s&aacute;ch n&agrave;y. Vậy n&ecirc;n mẹ c&oacute; thể nhẹ nh&otilde;m hơn v&igrave; m&igrave;nh c&ograve;n &ldquo;cứu vớt&rdquo; được một ch&uacute;t đồ ăn s&aacute;ng cho b&eacute; sau sự cố mất điện.</p>\r\n\r\n<p>Trứng để b&ecirc;n ngo&agrave;i c&oacute; thể sẽ kh&ocirc;ng c&oacute; vấn đề g&igrave; lớn. Nhưng một khi trứng đ&atilde; được bảo quản trong tủ lạnh, mẹ phải lu&ocirc;n giữ ch&uacute;ng trong m&ocirc;i trường lạnh ấy. Nếu nh&agrave; mất điện, to&agrave;n bộ trứng v&agrave; c&aacute;c sản phẩm từ trứng sẽ kh&ocirc;ng c&ograve;n an to&agrave;n để b&eacute; sử dụng nữa. Mẹ c&oacute; thể sẽ phải nghĩ ra một m&oacute;n mới thay v&igrave; m&oacute;n ch&aacute;o trứng y&ecirc;u th&iacute;ch của b&eacute;.</p>\r\n\r\n<p><strong>Hoa quả</strong></p>\r\n\r\n<p>C&aacute;c loại tr&aacute;i c&acirc;y tươi để trong tủ lạnh ho&agrave;n to&agrave;n c&oacute; thể ăn được nếu kh&ocirc;ng c&oacute; hơi m&aacute;t tủ lạnh trong một v&agrave;i tiếng. Tuy nhi&ecirc;n, nếu mẹ đ&atilde; cắt hoa quả ra th&agrave;nh từng miếng nhỏ, chỗ đồ ăn n&agrave;y sẽ kh&ocirc;ng c&ograve;n c&oacute; gi&aacute; trị sử dụng sau khi nh&agrave; mất điện.</p>\r\n\r\n<p>Tốt nhất, mẹ n&ecirc;n cắt sẵn một lượng hoa quả vừa đủ cho mỗi bữa ăn của b&eacute; v&agrave; kh&ocirc;ng để thừa lại trong tủ lạnh.</p>\r\n\r\n<p><strong>C&aacute;c loại sốt</strong></p>\r\n\r\n<p>Thật kh&ocirc;ng may, nếu tủ lạnh kh&ocirc;ng được cung cấp điện trong v&ograve;ng 8 tiếng, c&oacute; một số loại sốt sẽ kh&ocirc;ng thể &ldquo;trụ&rdquo; nổi. Điển h&igrave;nh trong danh s&aacute;ch n&agrave;y l&agrave; c&aacute;c m&oacute;n sốt kem, mayonaise, sốt c&agrave; chua. C&oacute; thể mẹ sẽ phải đi si&ecirc;u thị sắm lại từ đầu những nguy&ecirc;n liệu n&agrave;y khi điện mất qua đ&ecirc;m.</p>\r\n\r\n<p><strong>Cơm, m&igrave; ống</strong></p>\r\n\r\n<p>Thật đ&aacute;ng tiếc khi phải đổ đi cả một b&aacute;t cơm hay một đĩa m&igrave; ống mẹ để d&agrave;nh cho bữa trưa h&ocirc;m sau. Nhưng khi nh&agrave; mất điện, tủ lạnh kh&ocirc;ng c&ograve;n hơi m&aacute;t, những loại thực phẩm n&agrave;y sẽ dễ d&agrave;ng bị thiu v&agrave; hỏng. Chắc hẳn b&eacute; sẽ kh&ocirc;ng muốn ăn những m&oacute;n n&agrave;y cho bữa trưa của m&igrave;nh.</p>\r\n\r\n<p><strong>Rau</strong></p>\r\n\r\n<p>C&aacute;c loại rau đ&atilde; được nấu ch&iacute;n, hay rau chưa chế biến nhưng đ&atilde; được th&aacute;i, rửa qua nước v&agrave; bọc trong t&uacute;i b&oacute;ng sẽ kh&ocirc;ng c&ograve;n giữ được độ tươi ngon cũng như chất dinh dưỡng sau một buổi mất điện. V&igrave; vậy&nbsp; nếu mẹ c&oacute; &yacute; định nấu ch&aacute;o rau cải cho b&eacute; th&igrave; h&atilde;y c&acirc;n nhắc lại việc sử dụng những loại rau n&agrave;y.</p>\r\n', '496f305bd4bffcf4d987a0b6029eca55.jpg', '1402387264');
INSERT INTO `news` VALUES ('24', ' Dấu hiệu của những em bé bị ‘còi’ Dấu hiệu của những em bé bị ‘còi’ ', 'Dau-hieu-cua-nhung-em-be-bi-coi-Dau-hieu-cua-nhung-em-be-bi-coi', '2', 'Nỗi sợ hãi con “còi”, con chậm phát triển là nỗi lo lắng, nỗi “ám ảnh” muôn thủa của các bà mẹ Việt. Nhiều chị em không tiếc công sức mua thực phẩm chức năng, thuốc bổ cùng những thực phẩm giàu chất dinh dưỡng nhất để nhồi nhét cho con cái với niềm mong mỏi có con bụ con béo. Cũng có không ít bà mẹ lại bị rơi vào trạng thái trầm cảm, căng thẳng, suy sụp chỉ vì chuyện cân nặng của con.Tuy nhiên, cơ sở nào, số liệu nào nói rằng trẻ đã bị suy dinh dưỡng thì lại không phải bà mẹ nào cũng biết rõ. Không phải cứ con mình bé hơn con hàng xóm tức là bé đã bị suy dinh dưỡng. Một bà mẹ thông thái phải là một bà mẹ hiểu con, hiểu đúng tình trạng sức khỏe con và hiểu rõ được những dấu hiệu cảnh báo trẻ 0-2 tuổi suy dinh dưỡng này.', '<p>Nỗi sợ h&atilde;i con &ldquo;c&ograve;i&rdquo;, con chậm ph&aacute;t triển l&agrave; nỗi lo lắng, nỗi &ldquo;&aacute;m ảnh&rdquo; mu&ocirc;n thủa của c&aacute;c b&agrave; mẹ Việt. Nhiều chị em kh&ocirc;ng tiếc c&ocirc;ng sức mua thực phẩm chức năng, thuốc bổ c&ugrave;ng những thực phẩm gi&agrave;u chất dinh dưỡng nhất để nhồi nh&eacute;t cho con c&aacute;i với niềm mong mỏi c&oacute; con bụ con b&eacute;o. Cũng c&oacute; kh&ocirc;ng &iacute;t b&agrave; mẹ lại bị rơi v&agrave;o trạng th&aacute;i trầm cảm, căng thẳng, suy sụp chỉ v&igrave; chuyện c&acirc;n nặng của con.Tuy nhi&ecirc;n, cơ sở n&agrave;o, số liệu n&agrave;o n&oacute;i rằng trẻ đ&atilde; bị suy dinh dưỡng th&igrave; lại kh&ocirc;ng phải b&agrave; mẹ n&agrave;o cũng biết r&otilde;. Kh&ocirc;ng phải cứ con m&igrave;nh b&eacute; hơn con h&agrave;ng x&oacute;m tức l&agrave; b&eacute; đ&atilde; bị suy dinh dưỡng. Một b&agrave; mẹ th&ocirc;ng th&aacute;i phải l&agrave; một b&agrave; mẹ hiểu con, hiểu đ&uacute;ng t&igrave;nh trạng sức khỏe con v&agrave; hiểu r&otilde; được những dấu hiệu cảnh b&aacute;o trẻ 0-2 tuổi suy dinh dưỡng n&agrave;y.</p>\r\n\r\n<p><strong>Chỉ số c&acirc;n nặng</strong></p>\r\n\r\n<p>Trọng lượng được coi như chỉ số quan trọng nhạy cảm nhất phản &aacute;nh sự tăng trưởng v&agrave; ph&aacute;t triển của trẻ em.&nbsp;T&igrave;nh trạng dinh dưỡng, khẩu phần ăn, lượng thức ăn trẻ ăn v&agrave;o sẽ thể hiện ngay trong chỉ số c&acirc;n năng&nbsp;Trọng lượng c&oacute; thể bị ảnh hưởng bởi nhiều yếu tố như: dinh dưỡng, thực phẩm bổ sung, ốm sốt hay bệnh tật.</p>\r\n\r\n<p>Một em b&eacute; sơ sinh b&igrave;nh thường c&acirc;n nặng trong ba th&aacute;ng đầu ti&ecirc;n sẽ ph&aacute;t triển nhanh nhất, thường v&agrave;o khoảng tăng 600-1.000 gram/ th&aacute;ng, tốt nhất l&agrave; kh&ocirc;ng dưới 600 gram trong 3 th&aacute;ng đầu.&nbsp;Giai đoạn 3-6 th&aacute;ng, trẻ sẽ tăng mỗi th&aacute;ng 600-800 gram.&nbsp;Giai đoạn 6 đến 12 th&aacute;ng mức tăng trung b&igrave;nh h&agrave;ng th&aacute;ng l&agrave; 300 gram.&nbsp;Khi trẻ đạt 1 tuổi, tốc độ tăng trưởng chậm sẽ lại đ&aacute;ng kể. T&iacute;nh trung b&igrave;nh trẻ 1 đến 3 tuổi chỉ tăng khoảng 150 gram mỗi th&aacute;ng, c&oacute; b&eacute; nhiều th&aacute;ng kh&ocirc;ng tăng được lạng n&agrave;o.Sau n&agrave;y khi b&eacute; lớn, trọng lượng chuẩn sẽ được t&iacute;nh theo c&ocirc;ng thức: c&acirc;n nặng (kg) = tuổi (năm) x 2 +7 (hoặc 8). Những em b&eacute; kh&ocirc;ng đạt được chỉ số c&acirc;n nặng n&agrave;y sẽ c&oacute; dấu hiệu c&ograve;i, suy dinh dưỡng.</p>\r\n\r\n<p><strong>Chỉ số chiều cao</strong></p>\r\n\r\n<p>Chiều cao cũng l&agrave; một chỉ số quan trọng thể hiện sự tăng trưởng v&agrave; ph&aacute;t triển của trẻ. Tuy nhi&ecirc;n n&oacute; phản &aacute;nh t&igrave;nh trạng dinh dưỡng, tăng trưởng v&agrave; ph&aacute;t triển l&acirc;u d&agrave;i, kh&ocirc;ng bị ảnh hưởng hoặc ảnh hưởng kh&ocirc;ng r&otilde; r&agrave;ng bởi c&aacute;c yếu tố t&aacute;c động ngắn hạn như ốm sốt hay bệnh tật.</p>\r\n\r\n<p>Chiều cao của trẻ ph&aacute;t triển nhanh nhất trong năm đầu ti&ecirc;n sau khi sinh. T&iacute;nh trung b&igrave;nh một em b&eacute; b&igrave;nh thường năm đầu tăng tăng 25 cm, năm thứ 2 tăng 10 cm, năm thứ 3 tăng từ 4-7,5 cm.&nbsp;</p>\r\n\r\n<p>Những em b&eacute; kh&ocirc;ng đạt được c&aacute;c chỉ số chiều cao n&agrave;y cho thấy dấu hiệu của việc chậm ph&aacute;t triển thể chất.</p>\r\n\r\n<p><strong>Chu vi v&ograve;ng đầu</strong></p>\r\n\r\n<p>Chu vi v&ograve;ng đầu l&agrave; một chỉ số quan trọng thể hiện mức độ ph&aacute;t triển n&atilde;o bộ của trẻ.&nbsp;Chu vi v&ograve;ng đầu trong năm đầu ti&ecirc;n sau khi sinh ph&aacute;t triển nhanh nhất. Trung b&igrave;nh khi sinh chu vi đầu của trẻ sẽ l&agrave; 34cm, 1 tuổi đạt trung b&igrave;nh 46cm; năm thứ 2 tăng 2cm, năm thứ 3 tăng 1 ~ 2cm.&nbsp; Chu vi v&ograve;ng đầu khi trẻ đạt 3 tuổi trung b&igrave;nh sẽ l&agrave; 48cm, v&agrave; từ đ&oacute; đến khi trưởng th&agrave;nh sẽ kh&ocirc;ng c&oacute; nhiều sự kh&aacute;c biệt.&nbsp;</p>\r\n\r\n<p>Tuy nhi&ecirc;n mẹ cũng cần lưu &yacute; rằng kh&ocirc;ng phải cứ 100% trẻ đầu to l&agrave; học giỏi v&agrave; k&iacute;ch thước đầu kh&ocirc;ng phải l&agrave; tỷ lệ thuận tr&iacute; th&ocirc;ng minh .&nbsp;Chu vi v&ograve;ng đầu của trẻ n&ecirc;n ở trong phạm vi b&igrave;nh thường, kh&ocirc;ng n&ecirc;n qu&aacute; nhỏ, cũng kh&ocirc;ng n&ecirc;n to bất thường.&nbsp;Chu vi v&ograve;ng đầu qu&aacute; lớn sẽ phải xem x&eacute;t đến c&aacute;c trường hợp u n&atilde;o, n&atilde;o &uacute;ng thủy.</p>\r\n\r\n<p>Trẻ kh&ocirc;ng đạt chu vi v&ograve;ng đầu theo những chỉ số tr&ecirc;n cần được c&acirc;n nhắc về t&igrave;nh trạng suy dinh dưỡng.</p>\r\n\r\n<p><strong>Tỷ lệ v&ograve;ng ngực v&agrave; v&ograve;ng đầu</strong></p>\r\n\r\n<p>L&uacute;c mới sinh, v&ograve;ng ngực nhỏ hơn v&ograve;ng đầu 1-2cm. Dần dần c&ugrave;ng với sự lớn l&ecirc;n của trẻ, v&ograve;ng ngực sẽ đuổi kịp v&ograve;ng đầu, đến th&aacute;ng thứ 12 th&igrave; gần tương đương. Những b&eacute; c&oacute; t&igrave;nh trạng dinh dưỡng tốt, thậm ch&iacute; chưa đến 1 tuổi v&ograve;ng ngực v&agrave; v&ograve;ng đầu sẽ bằng nhau. Sau khi được 1 tuổi, v&ograve;ng ngực sẽ ph&aacute;t triển nhanh hơn, tăng đ&aacute;ng kể so với v&ograve;ng đầu. Những trẻ bị suy dinh dưỡng sẽ l&agrave; những em b&eacute; c&oacute; tỷ lệ v&ograve;ng ngực bằng v&ograve;ng đầu muộn.</p>\r\n', 'de9b0416602e2a5f1de190dd31f75ffb.jpg', '1402387780');
INSERT INTO `news` VALUES ('25', 'Thông báo vẽ tranh biển đảo', 'Thong-bao-ve-tranh-bien-dao', '1', 'Thông báo vẽ tranh biển đảo', '<p>TH&Ocirc;NG B&Aacute;O<br />\r\nV/v: Tổ chức vẽ tranh gi&aacute;o dục l&ograve;ng y&ecirc;u nước<br />\r\nK&iacute;nh gửi Qu&yacute; phụ huynh học sinh,<br />\r\nT&igrave;nh y&ecirc;u qu&ecirc; hương đất nước lu&ocirc;n tồn tại trong tr&aacute;i tim người d&acirc;n Việt Nam. Niềm tự h&agrave;o về một d&acirc;n tộc anh h&ugrave;ng được truyền từ thế hệ n&agrave;y sang thế hệ kh&aacute;c. Ho&agrave; c&ugrave;ng kh&ocirc;ng kh&iacute; phản đối Trung Quốc x&acirc;m lược đang sục s&ocirc;i tr&ecirc;n cả nước, trường MN học viện IQ tổ chức cho tập thể CB-GV-NV c&ugrave;ng phụ huynh v&agrave; học sinh to&agrave;n trường vẽ 01 bức tranh lớn về quần đảo Ho&agrave;ng Sa v&agrave; Trường Sa để khẳng định chủ quyền Việt Nam đồng thời gi&aacute;o dục l&ograve;ng y&ecirc;u nước đến c&aacute;c b&eacute;.<br />\r\n&bull; &nbsp; &nbsp;Thời gian: 8h30 &rarr;10h, ng&agrave;y 30 th&aacute;ng 6 (Thứ s&aacute;u)<br />\r\n&bull; &nbsp; &nbsp;Địa điểm: Tại tiền sảnh nh&agrave; trường<br />\r\nK&iacute;nh mời Qu&yacute; phụ huynh tham gia c&ugrave;ng c&aacute;c con.<br />\r\nTr&acirc;n trọng!<br />\r\nĐ&agrave; Nẵng, ng&agrave;y 20 th&aacute;ng 5 năm 2014<br />\r\nBGH nh&agrave; trường</p>\r\n\r\n<p>MẦM NON BẢO NGỌC<br />\r\nĐ/c: Số 153 T&ocirc;n Đản &ndash; Quận Li&ecirc;n Chiểu &ndash; TP Đ&agrave; Nẵng<br />\r\nTel: 05116 265 275<br />\r\nEmail: 123@yahoo.com<br />\r\nWeb: http://mamnonbaongoc.edu.vn/</p>\r\n', '7c71f9002335417250f54bf48771bd8a.jpg', '1402388397');
INSERT INTO `news` VALUES ('26', 'Thấp thỏm vì lỡ cho con ăn váng sữa ', 'Thap-thom-vi-lo-cho-con-an-vang-sua', '2', 'Bé nhà mình sang tuần sẽ tròn 5 tháng tuổi. Vì chuyện gia đình, mình bị trầm cảm nặng nề sau khi sinh và không có sữa cho con bú. Không còn cách nào khác, mình đành cho Bông, con gái mình uống sữa ngoài. Vẫn biết con thiệt thòi, nên mình luôn cố gắng chọn loại sữa tốt nhất cho con. Vậy mà đến giờ khi Bông được gần 5 tháng, bé chỉ nặng chưa đầy 6kg làm mình rất buồn và lo lắng. Sợ chỉ ăn sữa không đủ cho sự phát triển của con, mình quyết định cho bé ăn dặm sớm hơn các bạn cùng lứa.', '<p>B&eacute; nh&agrave; m&igrave;nh sang tuần sẽ tr&ograve;n 5 th&aacute;ng tuổi. V&igrave; chuyện gia đ&igrave;nh, m&igrave;nh bị trầm cảm nặng nề sau khi sinh v&agrave; kh&ocirc;ng c&oacute; sữa cho con b&uacute;. Kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o kh&aacute;c, m&igrave;nh đ&agrave;nh cho B&ocirc;ng, con g&aacute;i m&igrave;nh uống sữa ngo&agrave;i. Vẫn biết con thiệt th&ograve;i, n&ecirc;n m&igrave;nh lu&ocirc;n cố gắng chọn loại sữa tốt nhất cho con. Vậy m&agrave; đến giờ khi B&ocirc;ng được gần 5 th&aacute;ng, b&eacute; chỉ nặng chưa đầy 6kg l&agrave;m m&igrave;nh rất buồn v&agrave; lo lắng. Sợ chỉ ăn sữa kh&ocirc;ng đủ cho sự ph&aacute;t triển của con, m&igrave;nh quyết định cho b&eacute; ăn dặm sớm hơn c&aacute;c bạn c&ugrave;ng lứa.</p>\r\n\r\n<p>Sau khi ra si&ecirc;u thị được c&aacute;c bạn nh&acirc;n vi&ecirc;n tư vấn, m&igrave;nh mua cho con mấy hộp v&aacute;ng sữa cho b&eacute; ăn thử. M&igrave;nh nghĩ, loại thực phẩm n&agrave;y c&oacute; vị b&eacute;o ngậy, chắc B&ocirc;ng sẽ rất th&iacute;ch. Hơn nữa, m&igrave;nh nghe n&oacute;i v&aacute;ng sữa c&oacute; nhiều chất, b&eacute; ăn v&agrave;o c&oacute; lẽ c&acirc;n nặng sẽ cải thiện đ&aacute;ng kể.</p>\r\n\r\n<p>Tối h&ocirc;m ấy, m&igrave;nh quyết định cho B&ocirc;ng ăn lu&ocirc;n. B&eacute; ăn rất ngon l&agrave;nh l&agrave;m m&igrave;nh cũng mừng. Cho b&eacute; ăn v&agrave; ngủ xong, m&igrave;nh liền gọi điện khoe &ldquo;chiến t&iacute;ch&rdquo; với b&agrave; ngoại B&ocirc;ng. M&igrave;nh định hỏi b&agrave; xem 1 ng&agrave;y cho b&eacute; ăn 1 hộp c&oacute; &iacute;t kh&ocirc;ng, v&igrave; nếu t&iacute;nh ra, 1 hộp v&aacute;ng sữa chưa bằng 1 nửa bữa sữa của b&eacute;. Chưa kịp n&oacute;i hết c&acirc;u, b&agrave; đ&atilde; ngắt lời v&agrave; mắng m&igrave;nh sao &eacute;p con ăn loại thực phẩm v&ocirc; bổ ấy. B&agrave; n&oacute;i trong v&aacute;ng sữa to&agrave;n l&agrave; chất bảo quản, chất b&eacute;o rồi c&ograve;n &ldquo;ngọt như đường&rdquo;, cho B&ocirc;ng ăn như vậy kh&ocirc;ng chừng b&eacute; bị b&eacute;o ph&igrave;.</p>\r\n\r\n<p>M&igrave;nh thấy c&oacute; nhiều mẹ vẫn mua v&aacute;ng sữa nhập khẩu cho b&eacute; ăn mỗi bữa. Giờ nghe b&agrave; n&oacute;i vậy, m&igrave;nh cũng hoang mang kh&ocirc;ng biết v&aacute;ng sữa c&oacute; lợi hay hại cho b&eacute;. C&aacute;c mẹ c&oacute; kinh nghiệm c&oacute; thể cho m&igrave;nh lời khuy&ecirc;n được kh&ocirc;ng?</p>\r\n', '8eaf219e47bf2e066edf2290f40ca45b.jpg', '1402388551');
INSERT INTO `news` VALUES ('27', 'Thông báo tổ chức sự kiện tại Metro-Đà nẵng', 'Thong-bao-to-chuc-su-kien-tai-Metro-Da-nang', '1', 'Thông báo tổ chức sự kiện tại Metro-Đà nẵng', '<p>TH&Ocirc;NG B&Aacute;O<br />\r\nV/v: Tổ chức sự kiện tại Metro H&agrave; Đ&ocirc;ng<br />\r\nK&iacute;nh gửi Qu&yacute; phụ huynh học sinh,<br />\r\nNh&acirc;n dịp Quốc tế thiếu nhi 1/6, C&ocirc;ng ty &nbsp;METRO Cash &amp; Carry &nbsp;kết hợp với Trường MN Học viện IQ v&agrave; trường MN Yết Ki&ecirc;u tổ chức chương tr&igrave;nh sự kiện Vui tết thiếu nhi &ndash; B&eacute; l&agrave;m đầu bếp.<br />\r\nChương tr&igrave;nh bao gồm c&aacute;c tiết mục sau:<br />\r\n1. &nbsp; &nbsp;Thi l&agrave;m b&aacute;nh<br />\r\n2. &nbsp; &nbsp;Biểu diễn văn nghệ:<br />\r\n- &nbsp; &nbsp;H&aacute;t tiếng Anh<br />\r\n- &nbsp; &nbsp;Aerobic<br />\r\n- &nbsp; &nbsp;Biểu diễn thời trang.<br />\r\n3. &nbsp; &nbsp;Tặng qu&agrave; cho c&aacute;c b&eacute;.<br />\r\n&bull; &nbsp; &nbsp;Thời gian: 18h &rarr; 19h ng&agrave;y 30 th&aacute;ng 5 (Thứ s&aacute;u)<br />\r\n&bull; &nbsp; &nbsp;Địa điểm: Tại Trung t&acirc;m TM v&agrave; DVTM M&ecirc; Linh Plaza H&agrave; Đ&ocirc;ng.<br />\r\nK&iacute;nh mời Qu&yacute; phụ huynh c&ugrave;ng c&aacute;c con tham gia v&agrave; cổ vũ cho chương tr&igrave;nh.<br />\r\nTr&acirc;n trọng!<br />\r\nĐ&agrave; Nẵng, ng&agrave;y 20 th&aacute;ng 5 năm 2014<br />\r\nBGH nh&agrave; trường</p>\r\n', '7145a627382caabbf475f1c364f2b2e5.jpg', '1402388792');
INSERT INTO `news` VALUES ('30', 'Thông báo tuyển sinh mầm non năm học 2014-2015', 'Thong-bao-tuyen-sinh-mam-non-nam-hoc-2014-2015', '1', 'Thông báo tuyển sinh mầm non năm học 2014-2015', '<p>TH&Ocirc;NG B&Aacute;O TUYỂN SINH<br />\r\nV/v Tuyển sinh độ tuổi nh&agrave; trẻ, mẫu gi&aacute;o<br />\r\nnăm học 2014-2015<br />\r\nCăn cứ kế hoạch ph&aacute;t triển gi&aacute;o dục năm học 2014 -2015 của Ph&ograve;ng Gi&aacute;o dục-Đ&agrave;o tạo Quận Thanh Kh&ecirc;. Ban L&atilde;nh đạo trường Mầm non Tư thục Hồng Nhung th&ocirc;ng b&aacute;o kế hoạch tuyển sinh &nbsp;của nh&agrave; trường ở c&aacute;c độ tuổi như sau :&nbsp;<br />\r\nI. Đối tượng tuyển sinh :<br />\r\n&nbsp; &nbsp; Nh&agrave; trường tuyển sinh c&aacute;c ch&aacute;u ở độ tuổi nh&agrave; trẻ từ 18-24 th&aacute;ng, sinh năm 2012 v&agrave; c&aacute;c ch&aacute;u độ tuổi Mẫu gi&aacute;o b&eacute; sinh năm 2011, kh&ocirc;ng tuyển sinh c&aacute;c ch&aacute;u ở độ tuổi sinh năm 2010 v&agrave; sinh năm 2009.<br />\r\nII. Thời gian ph&aacute;t v&agrave; nhận hồ sơ - Ng&agrave;y nhập học :<br />\r\n&nbsp;* Số hồ sơ ph&aacute;t : 122 bộ<br />\r\nTr&acirc;n trọng!<br />\r\nĐ&agrave; Nẵng, ng&agrave;y 20 th&aacute;ng 5 năm 2014<br />\r\nBGH nh&agrave; trường</p>\r\n\r\n<p>MẦM NON BẢO NGỌC<br />\r\nĐ/c: Số 153 T&ocirc;n Đản &ndash; Quận Li&ecirc;n Chiểu &ndash; TP Đ&agrave; Nẵng<br />\r\nTel: 05116 265 275<br />\r\nEmail: 123@yahoo.com<br />\r\nWeb: http://mamnonbaongoc.edu.vn/</p>\r\n', '3a42f16daf8b4e9cc49717909d8bdd87.jpg', '1402389178');
INSERT INTO `news` VALUES ('31', 'THÔNG BÁO PHÒNG TRÁNH DỊCH SỞI CÙNG Mầm non Bảo Ngọc', 'THONG-BAO-PHONG-TRANH-DICH-SOI-CUNG-Mam-non-Bao-Ngoc', '3', 'Trước tình hình bệnh sởi đang diễn biến phức tạp, để đảm bảo an toàn về sức khỏe cho các bé, Mầm non Bảo Ngọc xin trân trọng thông báo tới Quý phụ huynh', '<p style=\"text-align:justify\"><strong><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">Trước t&igrave;nh h&igrave;nh bệnh sởi đang diễn biến phức tạp, để đảm bảo an to&agrave;n về sức khỏe cho c&aacute;c b&eacute;, Mầm non Bảo Ngọc xin tr&acirc;n trọng th&ocirc;ng b&aacute;o tới Qu&yacute; phụ huynh:</span></strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\"><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">*Những biện ph&aacute;p ph&ograve;ng tr&aacute;nh dịch sởi của Mầm non Bảo Ngọc:</span></span></p>\r\n\r\n<p><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">- Hiện tại nh&agrave; trường đ&atilde; c&oacute; c&aacute;c biện ph&aacute;p ph&ograve;ng tr&aacute;nh dịch sởi như vệ sinh ph&ograve;ng học, đồ chơi cho c&aacute;c b&eacute;, khử tr&ugrave;ng c&aacute;c khu vực trong trường học nơi b&eacute; vui chơi giải tr&iacute;. </span><br />\r\n<span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">- To&agrave;n bộ gi&aacute;o vi&ecirc;n Mầm non Bảo Ngọc đ&atilde; được </span><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">họp v&agrave; truyền đạt lại c&aacute;c biện ph&aacute;p ph&ograve;ng tr&aacute;nh dịch sởi cho b&eacute; từ nh&acirc;n vi&ecirc;n y tế của trường.<br />\r\n- Chế độ dinh dưỡng của c&aacute;c b&eacute; h&agrave;ng ng&agrave;y cũng đ&atilde; tăng cường lượng vitamin C trong hoa quả, vitamin A trong thức ăn để tăng cường sức đề kh&aacute;ng cho b&eacute;.<br />\r\n- C&aacute;c b&eacute; c&oacute; hiện tượng ốm, sốt sẽ được c&aacute;ch ly với c&aacute;c b&eacute; kh&aacute;c v&agrave; nh&agrave; trường sẽ th&ocirc;ng b&aacute;o ngay cho ba mẹ đ&oacute;n để b&eacute; được kh&aacute;m trong thời gian sớm nhất. </span></p>\r\n\r\n<p><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\"><strong>Lưu &yacute; d&agrave;nh cho Qu&yacute; phụ huynh:</strong></span></p>\r\n\r\n<p><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">- Nh&agrave; trường cũng khuyến c&aacute;o phụ huynh cho c&aacute;c b&eacute; đi ti&ecirc;m ph&ograve;ng dịch sởi, ph&ograve;ng bệnh chủ động bằng vắc xin theo hướng dẫn của cơ quan chuy&ecirc;n m&ocirc;n.<br />\r\n- Hạn chế tiếp x&uacute;c nguồn bệnh, hạn chế tập trung nơi đ&ocirc;ng người v&agrave; c&aacute;c nơi c&ocirc;ng cộng.<br />\r\n- Người bệnh, người chăm s&oacute;c bệnh nh&acirc;n sởi, nh&acirc;n vi&ecirc;n y tế cần sử dụng khẩu trang y tế.<br />\r\n- N&acirc;ng cao thể trạng, ăn uống dinh dưỡng đầy đủ. </span></p>\r\n\r\n<p><span style=\"background-color:rgb(255,255,255); color:rgb(51,51,51)\">Rất mong qu&yacute; vị phụ huynh t&iacute;ch cực hợp t&aacute;c c&ugrave;ng BachvietSchool trong việc thực hiện c&aacute;c biện ph&aacute;p ph&ograve;ng tr&aacute;nh dịch sởi để đảm bảo an to&agrave;n sức khỏe cho b&eacute;.<br />\r\n<br />\r\n<em>Xin ch&acirc;n th&agrave;nh cảm ơn!</em></span></p>\r\n', 'a7a0a0a60f6602ee18321f6257f61c64.jpg', '1402389276');
INSERT INTO `news` VALUES ('32', 'Thông báo nghỉ tết', 'Thong-bao-nghi-tet', '1', 'Thông báo nghỉ tết', '<p>TH&Ocirc;NG B&Aacute;O NGHỈ TẾT<br />\r\nNg&agrave;y tết đang đến gần &ldquo;Tết Nguy&ecirc;n Đ&aacute;n&rdquo; mọi người bảo với b&eacute; như vậy. B&eacute; cảm thấy thật vui v&agrave; n&aacute;o nức. Kh&ocirc;ng kh&iacute; ở mọi nơi trong những ng&agrave;y n&agrave;y thật rộn r&atilde;, ở nh&agrave; th&igrave; b&agrave; v&agrave; mẹ l&agrave;m b&aacute;nh, mứt thơm phức, bố th&igrave; hối hả dọn nh&agrave;, trang tr&iacute; tổ ấm của b&eacute; thật đẹp, &ocirc;ng th&igrave; chăm s&oacute;c c&acirc;y cảnh, g&oacute;i b&aacute;nh t&eacute;t thật l&agrave; vui...<br />\r\nTr&acirc;n trọng!<br />\r\nĐ&agrave; Nẵng, ng&agrave;y 20 th&aacute;ng 5 năm 2014<br />\r\nBGH nh&agrave; trường</p>\r\n\r\n<p>MẦM NON BẢO NGỌC<br />\r\nĐ/c: Số 153 T&ocirc;n Đản &ndash; Quận Li&ecirc;n Chiểu &ndash; TP Đ&agrave; Nẵng<br />\r\nTel: 05116 265 275<br />\r\nEmail: 123@yahoo.com<br />\r\nWeb: http://mamnonbaongoc.edu.vn/</p>\r\n', 'd766c90c854fe5ff2fb387770b11b718.jpg', '1402389439');
INSERT INTO `news` VALUES ('33', 'Giải mã những \"bí ẩn\" đằng sau cái ôm của bé với mẹ', 'Giai-ma-nhung-bi-an-dang-sau-cai-om-cua-be-voi-me', '3', 'Đây có lẽ là cảm xúc tự nhiên nhất của bé mà mẹ thật sự mong chờ. Nó làm cho bạn có cảm giác mình là một phần vô cùng quan trọng trong cuộc sống của con. Trẻ con biểu lộ nhiều cảm xúc khi ôm hôn sẽ giàu lòng vị tha và sống tình cảm hơn khi trường thành. Hãy thử tưởng tượng khi bé vừa về nhà, khi nhìn thấy bạn chúng sẵn sàng cởi phắt áo khoác và nhảy chồm lên lòng bạn, ôm hôn bạn, ríu rít líu lo bên tai bạn như một chú chim non, bạn sẽ thấy lòng hạnh phúc đến nhường nào.\r\n', '<p style=\"text-align:justify\"><strong>Mẹ c&oacute; tin rằng mỗi c&aacute;i &ocirc;m của b&eacute; lại ẩn chứa những cung bậc t&igrave;nh cảm kh&aacute;c nhau kh&ocirc;ng? Mẹ h&atilde;y thử xem b&eacute; y&ecirc;u bạn ở mức độ n&agrave;o nh&eacute;!</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>1. B&eacute; cảm thấy: Ng&agrave;y h&ocirc;m nay thật d&agrave;i v&agrave; con muốn r&uacute;c v&agrave;o l&ograve;ng mẹ</strong></span><br />\r\n<br />\r\nĐ&acirc;y c&oacute; lẽ l&agrave; cảm x&uacute;c tự nhi&ecirc;n nhất của b&eacute; m&agrave; mẹ thật sự mong chờ. N&oacute; l&agrave;m cho bạn c&oacute; cảm gi&aacute;c m&igrave;nh l&agrave; một phần v&ocirc; c&ugrave;ng quan trọng trong cuộc sống của con. Trẻ con biểu lộ nhiều cảm x&uacute;c khi &ocirc;m h&ocirc;n sẽ gi&agrave;u l&ograve;ng vị tha v&agrave; sống t&igrave;nh cảm hơn khi trường th&agrave;nh. H&atilde;y thử tưởng tượng khi b&eacute; vừa về nh&agrave;, khi nh&igrave;n thấy bạn ch&uacute;ng sẵn s&agrave;ng cởi phắt &aacute;o kho&aacute;c v&agrave; nhảy chồm l&ecirc;n l&ograve;ng bạn, &ocirc;m h&ocirc;n bạn, r&iacute;u r&iacute;t l&iacute;u lo b&ecirc;n tai bạn như một ch&uacute; chim non, bạn sẽ thấy l&ograve;ng hạnh ph&uacute;c đến nhường n&agrave;o.<br />\r\n<br />\r\nMức độ th&agrave;nh thật: Những c&aacute;i &ocirc;m h&ocirc;n n&agrave;y v&ocirc; c&ugrave;ng ch&acirc;n thật v&agrave; hồn nhi&ecirc;n. B&eacute; như muốn n&oacute;i với bạn rằng: &ldquo; Con l&agrave; b&eacute; con của mẹ v&agrave; con cần ai đ&oacute; &ocirc;m con thật chặt v&agrave; đ&oacute; l&agrave; mẹ đấy&rdquo;. Tất nhi&ecirc;n, c&aacute;i &ocirc;m n&agrave;y sẽ k&eacute;o d&agrave;i đến tận mấy ph&uacute;t mới th&ocirc;i. L&uacute;c n&agrave;y mẹ chỉ c&oacute; thể im lặng, nghi&ecirc;ng đầu để lựa tư thế &ocirc;m b&eacute; thật dịu d&agrave;ng v&agrave; l&uacute;c n&agrave;y chẳng thể nghĩ đến một điều g&igrave; c&oacute; thể cắt ngang gi&acirc;y ph&uacute;t n&agrave;y được nữa.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>2. Con đang ở trường với c&aacute;c bạn v&agrave; &ocirc;m mẹ qua loa một c&aacute;i</strong></span><br />\r\n<br />\r\nNhững đứa trẻ ở tầm độ 4 đến 5 tuổi kh&ocirc;ng c&ograve;n ở độ tuổi &ldquo;b&aacute;m&rdquo; mẹ nữa m&agrave; ch&uacute;ng đ&atilde; c&oacute; nhiều th&uacute; vui kh&aacute;c v&agrave; sẵn s&agrave;ng phớt lờ mẹ. Thậm ch&iacute; ch&uacute;ng chỉ d&agrave;nh cho bạn một c&aacute;i &ocirc;m qua loa trước khi &ldquo;mắt s&aacute;ng l&ecirc;n&rdquo; với những tr&ograve; chơi hấp dẫn hơn nhiều với c&aacute;c bạn. B&eacute; cũng c&oacute; thể c&oacute; suy nghĩ rằng m&igrave;nh đ&atilde; lớn rồi n&ecirc;n &ocirc;m mẹ ở nơi nhiều người sẽ thấy ngượng ng&ugrave;ng xấu hổ.<br />\r\n<br />\r\nMức độ th&agrave;nh thật: Dĩ nhi&ecirc;n l&agrave; kh&ocirc;ng được thật l&ograve;ng lắm v&agrave; những c&aacute;i &ocirc;m n&agrave;y c&oacute; lẽ chỉ diễn ra trong khoảng v&agrave;i gi&acirc;y. L&uacute;c n&agrave;y c&oacute; lẽ mẹ sẽ hơi buồn một ch&uacute;t.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>3. Khi mẹ đi c&ocirc;ng t&aacute;c về v&agrave; mua qu&agrave; cho con</strong></span><br />\r\n<br />\r\nL&uacute;c n&agrave;y b&eacute; sẽ nhảy phốc l&ecirc;n l&ograve;ng hoặc &ocirc;m chầm lấy mẹ. C&aacute;i &ocirc;m n&agrave;y nghĩa l&agrave;: &ldquo;Con nhớ mẹ nhiều lắm, mẹ về rồi vui qu&aacute;&rdquo;.<br />\r\n<br />\r\nMức độ th&agrave;nh thật: Kh&ocirc;ng hẳn l&agrave; b&eacute; thật sự muốn d&agrave;nh cho mẹ những c&aacute;i &ocirc;m h&ocirc;n nồng nhiệt như vậy, v&igrave; c&oacute; một điều b&eacute; n&oacute;ng l&ograve;ng chờ đợi ch&iacute;nh l&agrave; qu&agrave; mẹ mua về cho m&igrave;nh. Một tay b&eacute; v&ograve;ng qua cổ &ocirc;m mẹ, c&ograve;n mắt th&igrave; vẫn&hellip; chăm ch&uacute; nh&igrave;n m&oacute;n qu&agrave; ở tay kia v&agrave; miệng hỏi kh&ocirc;ng ngớt những c&acirc;u xung quanh m&oacute;n qu&agrave;.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>4. Thấy anh chị b&eacute; &ocirc;m mẹ th&igrave; b&eacute; cũng &ocirc;m mẹ</strong></span><br />\r\n<br />\r\nĐ&acirc;y c&oacute; lẽ l&agrave; kiểu thể hiện t&igrave;nh cảm thường xuy&ecirc;n trong gia đ&igrave;nh bạn, khi m&agrave; b&eacute; muốn m&igrave;nh l&agrave; người y&ecirc;u thương mẹ hơn anh (chị, em) của m&igrave;nh. V&iacute; dụ như đứa lớn đang d&iacute; dỏm &ocirc;m mẹ tr&ograve; chuyện v&igrave; n&oacute; đang rất hạnh ph&uacute;c t&igrave;m được bạn mới ở trường, th&igrave; đứa b&eacute; nh&igrave;n thấy vội nhảy chồm l&ecirc;n &ocirc;m chặt lấy bạn.<br />\r\n<br />\r\nMức độ th&agrave;nh thật: Những chiếc &ocirc;m thế n&agrave;y chỉ chứng tỏ b&eacute; chỉ muốn l&agrave; trung t&acirc;m của mọi ch&uacute; &yacute;, hoặc &iacute;t nhất l&agrave; được mẹ ch&uacute; &yacute; hơn anh chị của m&igrave;nh. L&uacute;c n&agrave;y mẹ h&atilde;y nh&igrave;n v&agrave;o mắt mỗi b&eacute; v&agrave; n&oacute;i &ldquo; bố mẹ đều y&ecirc;u c&aacute;c con&rdquo; để tập th&oacute;i quen cho trẻ kh&ocirc;ng n&ecirc;n ch&agrave;nh chọe v&agrave; biết nhường nhịn mọi người.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>5. B&eacute; vừa nằm &ocirc;m mẹ vừa xem phim</strong></span><br />\r\n<br />\r\nThật l&agrave; tuyệt với cảm gi&aacute;c mẹ con c&ugrave;ng nằm &ocirc;m nhau tr&ecirc;n ghế sofa v&agrave; xem những bộ phim ưa th&iacute;ch.<br />\r\n<br />\r\nMức độ th&agrave;nh thật: Th&ocirc;ng thường, b&eacute; c&ograve;n nhỏ n&ecirc;n sẽ chỉ ch&uacute; &yacute; đến những bộ phim v&agrave; những c&aacute;i &ocirc;m l&uacute;c n&agrave;y chỉ l&agrave; qu&agrave;ng tay qua mẹ v&agrave; kh&ocirc;ng mang nhiều t&igrave;nh cảm. Tuy nhi&ecirc;n, nếu bộ phim chiếu đến những cảnh l&agrave;m b&eacute; sợ v&agrave; b&eacute; quay ra &ocirc;m chặt lấy mẹ th&igrave; những c&aacute;i &ocirc;m l&uacute;c n&agrave;y thể hiện b&eacute; đang sợ sệt v&agrave; coi mẹ như một người bảo vệ của m&igrave;nh để rồi tin tưởng nằm trong v&ograve;ng tay ấy. Hoặc nếu phim c&oacute; những&nbsp; ph&acirc;n cảnh về t&igrave;nh mẫu tử - khi 2 mẹ con &ocirc;m nhau chẳng hạn v&agrave; b&eacute; quay sang &ocirc;m mẹ m&igrave;nh, th&igrave; xin ch&uacute;c mừng bạn, bạn c&oacute; một đứa con nhạy cảm, ngọt ng&agrave;o v&agrave; lu&ocirc;n y&ecirc;u mẹ. Những c&aacute;i &ocirc;m l&uacute;c n&agrave;y thể hiện t&igrave;nh cảm ch&acirc;n thực nhất với như một c&aacute;ch b&eacute; n&oacute;i: &ldquo;Con y&ecirc;u mẹ rất rất nhiều&rdquo;.</p>\r\n', '7431178e51a1c69af7485aadf1c43b5e.jpg', '1402389366');
INSERT INTO `news` VALUES ('34', 'Xúc động bức thư của bố viết tặng con sắp chào đời', 'Xuc-dong-buc-thu-cua-bo-viet-tang-con-sap-chao-doi', '3', 'Bức thư có tiêu đề “From papa with love” là những dòng tâm sự, trăn trở thật lòng nhất của một người cha dành cho đứa con trai đầu lòng của mình.', '<p style=\"text-align:justify\"><strong>Bức thư c&oacute; ti&ecirc;u đề &ldquo;From papa with love&rdquo; l&agrave; những d&ograve;ng t&acirc;m sự, trăn trở thật l&ograve;ng nhất của một người cha d&agrave;nh cho đứa con trai đầu l&ograve;ng của m&igrave;nh.</strong></p>\r\n\r\n<p style=\"text-align:justify\">Con trai &agrave;,</p>\r\n\r\n<p style=\"text-align:justify\">Con c&ograve;n 2 ng&agrave;y nữa để c&acirc;n nhắc việc sẽ ch&agrave;o đời bằng c&aacute;ch n&agrave;o, h&atilde;y suy nghĩ v&agrave; cho bố mẹ biết t&iacute;n hiệu nh&eacute;. 9 th&aacute;ng 10 ng&agrave;y trong bụng ấm của mẹ l&agrave; &quot;maximum&quot; rồi. Giờ l&agrave; l&uacute;c con phải bắt đầu một cuộc sống như bao người kh&aacute;c th&ocirc;i.</p>\r\n\r\n<p style=\"text-align:justify\">Con sẽ bỡ ngỡ lắm đấy bởi n&oacute; sẽ kh&aacute;c rất nhiều với cuộc sống của con trong hơn 9 th&aacute;ng vừa qua. Nếu như hiện tại con vẫn đang phải nằm co lại, chật chội th&igrave; sắp tới sẽ được duỗi ch&acirc;n, duỗi tay thoải m&aacute;i. Nếu như trước giờ con chỉ biết lắng nghe v&agrave; phản ứng bằng c&aacute;ch đạp đạp ch&acirc;n th&igrave; sắp tới sẽ được &quot;c&oacute; &yacute; kiến&quot; hẳn hoi mỗi khi kh&ocirc;ng vừa l&ograve;ng. Từ việc cả ng&agrave;y chỉ nh&igrave;n thấy một m&agrave;u tối om xung quanh th&igrave; rồi con sẽ được thấy &aacute;nh s&aacute;ng mặt trời, bằng rất nhiều m&agrave;u sắc rực rỡ v&agrave; bằng những c&aacute;i nh&igrave;n &acirc;u yếm ngập tr&agrave;n y&ecirc;u thương của mẹ, của bố, của &ocirc;ng, của b&agrave;... Thực ra c&oacute; một điều m&agrave; chắc chắn cuộc sống ngo&agrave;i n&agrave;y kh&ocirc;ng thể n&agrave;o s&aacute;nh được với khi c&ograve;n trong bụng mẹ, đ&oacute; l&agrave; sự y&ecirc;n b&igrave;nh. Nhưng con đừng lo, bố biết c&aacute;ch để giữ y&ecirc;n b&igrave;nh cho con.</p>\r\n\r\n<p style=\"text-align:justify\">Con biết kh&ocirc;ng, bố mẹ, &ocirc;ng b&agrave; nội ngoại đ&atilde; chuẩn bị cho con rất nhiều đồ đạc đấy. Từ b&igrave;nh sữa, n&ocirc;i, chậu tắm, sữa tắm, quần &aacute;o đẹp, bao tay, b&iacute;t tất... cho đến những c&aacute;i ngo&aacute;y tai nhỏ x&iacute;u cho vừa với tai của con. B&agrave; nội th&igrave; đ&atilde; v&agrave; đang ho&agrave;n th&agrave;nh dần &quot;bộ sưu tập&quot; &aacute;o len b&agrave; đan cho con, b&agrave; ngoại th&igrave; &quot;trực chiến 24/24&quot; đi ra đi v&agrave;o nghe ng&oacute;ng... bố tin con cũng cảm nhận được v&agrave; sau n&agrave;y sẽ ngoan ngo&atilde;n nghe lời &ocirc;ng b&agrave;, bố mẹ.</p>\r\n\r\n<p style=\"text-align:justify\">V&agrave; cũng nhờ c&oacute; con m&agrave; mọi người tạm qu&ecirc;n những phiền muộn kh&aacute;c từ cuộc sống.</p>\r\n\r\n<p style=\"text-align:justify\">Thay cho những &aacute;p lực vật l&yacute;, sinh l&yacute; học trong bụng mẹ, khi ra đời mọi đứa trẻ thường sẽ phải chịu những &aacute;p lực kh&aacute;c thay thế. B&eacute; th&igrave; &aacute;p lực ăn ngủ đ&uacute;ng giờ, &aacute;p lực phải ngoan cả ng&agrave;y kh&ocirc;ng được kh&oacute;c, lớn hơn ch&uacute;t th&igrave; &aacute;p lực phải biết tự x&uacute;c cơm ăn hay ăn cơm kh&ocirc;ng được... ngậm, rồi th&igrave; phải nhai kỹ hay thậm ch&iacute; l&agrave; &aacute;p lực phải biết n&oacute;i hoặc ra dấu mỗi khi... buồn t&egrave;. Lớn nữa l&agrave; &aacute;p lực từ học h&agrave;nh v&agrave; &aacute;p lực của những kỳ vọng m&agrave; &ocirc;ng b&agrave;, bố mẹ đặt v&agrave;o m&igrave;nh... v&agrave; c&ograve;n nhiều loại &aacute;p lực kh&aacute;c nữa. Đấy l&agrave; những &aacute;p lực m&agrave; theo bố l&agrave; kh&ocirc;ng tr&aacute;nh được con ạ, nhưng bố sẽ dạy con c&aacute;ch sống chung với ch&uacute;ng, biến ch&uacute;ng th&agrave;nh điểm tựa của th&agrave;nh c&ocirc;ng sau n&agrave;y.</p>\r\n\r\n<p style=\"text-align:justify\">Bố kh&ocirc;ng hứa được chắc chắn sẽ cho con một cuộc sống m&agrave;u hồng (m&agrave;u hồng giải th&iacute;ch để con biết l&agrave; m&agrave;u m&agrave; mọi người thường v&iacute; với sự no đủ v&agrave; sung sướng) v&igrave; thực sự bố chẳng c&oacute; g&igrave; để cho con, nhưng bố chắc chắn sẽ hướng dẫn được để gi&uacute;p con c&oacute; được một cuộc sống như vậy. Bức tranh cuộc sống của con bố chẳng thể vẽ thay, nhưng bố c&oacute; thể cho con b&uacute;t v&agrave; giấy v&agrave; bố tin con sẽ vẽ rất đẹp.</p>\r\n\r\n<p style=\"text-align:justify\">Sống tr&ecirc;n đời điều hạnh ph&uacute;c nhất l&agrave; được theo đuổi đam m&ecirc; của m&igrave;nh, v&agrave; ho&agrave;n mỹ nhất l&agrave; đam m&ecirc; đ&oacute; đảm bảo được về mặt kinh tế cho bản th&acirc;n, gia đ&igrave;nh. Do đ&oacute;, nếu như đ&acirc;u đ&oacute; vẫn c&ograve;n nhiều gia đ&igrave;nh mong muốn con c&aacute;i sau n&agrave;y phải trở th&agrave;nh b&aacute;c sỹ, gi&aacute;o sư, th&igrave; với bố, con sẽ được tự do theo đuổi sở th&iacute;ch của m&igrave;nh, miễn l&agrave; phải cố gắng theo đuổi đến c&ugrave;ng v&agrave; trở th&agrave;nh người giỏi nhất trong lĩnh vực đ&oacute;. Nếu l&agrave; họa sỹ th&igrave; phải c&oacute; t&aacute;c phẩm để đời. Nếu l&agrave; nh&agrave; văn th&igrave; phải để lại t&ecirc;n tuổi. Nếu l&agrave; cầu thủ b&oacute;ng đ&aacute; th&igrave; phải thi đấu cho Manchester United. Hoặc nếu l&agrave; t&agrave;i xế th&igrave; phải đảm bảo an to&agrave;n tuyệt đối cho h&agrave;nh kh&aacute;ch. &Aacute;p lực kh&ocirc;ng? C&oacute; chứ, rất nhiều, nhưng l&agrave; &aacute;p lực cần c&oacute; m&agrave; con n&ecirc;n tự tạo cho m&igrave;nh nếu muốn thực sự trưởng th&agrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\">Để đạt được những điều đ&oacute;, theo kinh nghiệm từ những đổ vỡ của bố th&igrave; đầu ti&ecirc;n con phải học c&aacute;ch sống c&oacute; tr&aacute;ch nhiệm, phải l&agrave; một người đ&agrave;n &ocirc;ng c&oacute; tr&aacute;ch nhiệm với bản th&acirc;n v&agrave; gia đ&igrave;nh m&igrave;nh. Tr&aacute;ch nhiệm với những việc l&agrave;m của m&igrave;nh. Khi đ&oacute; con sẽ l&agrave; một người đ&agrave;n &ocirc;ng thực thụ.</p>\r\n\r\n<p style=\"text-align:justify\">V&agrave; một trong những tr&aacute;ch nhiệm cơ bản đầu ti&ecirc;n của một người đ&agrave;n &ocirc;ng trưởng th&agrave;nh l&agrave; kh&ocirc;ng để người th&acirc;n y&ecirc;u phải lo lằng, v&igrave; vậy h&atilde;y kh&oacute;c thật to khi ch&agrave;o đời để bố mẹ thấy an t&acirc;m về con ngay từ những ph&uacute;t gi&acirc;y đầu ti&ecirc;n nh&eacute;.</p>\r\n\r\n<p style=\"text-align:justify\">Hẹn gặp con 2 ng&agrave;y nữa, con trai của bố!</p>\r\n', '40162f28a279b701b35e891a28122055.jpg', '1402389768');
INSERT INTO `news` VALUES ('35', 'Giúp con học toán bằng các trò chơi trong bếp', 'Giup-con-hoc-toan-bang-cac-tro-choi-trong-bep', '3', 'Những hướng dẫn dưới đây từ các chuyên gia giáo dục sẽ giúp bạn dạy con học toán một cách vui thích trong gian bếp của gia đình.', '<p><strong>Những hướng dẫn dưới đ&acirc;y từ c&aacute;c chuy&ecirc;n gia gi&aacute;o dục sẽ gi&uacute;p bạn dạy con học to&aacute;n một c&aacute;ch vui th&iacute;ch trong gian bếp của gia đ&igrave;nh.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>Đếm, cộng, trừ</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">B&eacute; 3-4 tuổi c&oacute; thể thực h&agrave;nh kỹ năng đếm, chẳng hạn c&oacute; bao nhi&ecirc;u quả d&acirc;u t&acirc;y, bao nhi&ecirc;u chiếc b&aacute;nh ngọt, c&oacute; mấy người ngồi xung quanh b&agrave;n ăn.</p>\r\n\r\n<p style=\"text-align:justify\">Với b&eacute; lớn hơn ch&uacute;t, bạn c&oacute; thể rải một &iacute;t hạt ngũ cốc kh&ocirc; l&ecirc;n b&agrave;n rồi cho b&eacute; đếm, tiếp tục th&ecirc;m v&agrave;i hạt hay bớt v&agrave;i hạt v&agrave; bảo b&eacute; l&agrave;m ph&eacute;p t&iacute;nh cộng, trừ. Chẳng hạn, mẹ c&oacute; 10 hạt lạc, giờ mẹ cho th&ecirc;m 3 hạt nữa, con xem c&oacute; mấy hạt tất cả?</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>H&igrave;nh học</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c loại thực phẩm c&oacute; đủ h&igrave;nh dạng v&agrave; k&iacute;ch thước. H&atilde;y dạy b&eacute; c&aacute;ch nhận dạng c&aacute;c h&igrave;nh dạng từ những loại đồ ăn: H&igrave;nh vu&ocirc;ng (b&aacute;nh ngọt), h&igrave;nh tam gi&aacute;c (một l&aacute;t d&acirc;u t&acirc;y), h&igrave;nh tr&ograve;n (b&aacute;t đựng ch&aacute;o), h&igrave;nh ovan (quả trứng)...</p>\r\n\r\n<p style=\"text-align:justify\">Bạn c&oacute; thể cho b&eacute; d&ugrave;ng ng&oacute;n tay để tự vẽ l&ecirc;n những h&igrave;nh b&eacute; nghĩ ra. Nếu mẹ kh&eacute;o tay, c&oacute; thể cắt tỉa từ củ quả cho con xem c&aacute;c h&igrave;nh kh&aacute;c như h&igrave;nh tr&aacute;i tim, h&igrave;nh ng&ocirc;i sao...</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>Đơn vị đo lường</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Bạn c&oacute; thể gi&uacute;p trẻ học về học về c&aacute;c đơn vị đo lường trong l&uacute;c nấu nướng: N&oacute;i về c&aacute;ch đong c&aacute;c chất lỏng như d&ugrave;ng cốc (đựng nước), th&igrave;a (đựng mắm)... giới thiệu cho con biết về c&aacute;c đơn vị kh&aacute;c như gam, kilogam... Bạn cũng c&oacute; thể dạy con những đơn vị đo lường lớn hơn, v&agrave; n&oacute;i cho b&eacute; nghe những kh&aacute;i niệm đầu ti&ecirc;n về số thập ph&acirc;n như: một nửa cốc nước, 1/3 cốc nước... v&agrave; chỉ cho con thấy sự kh&aacute;c biệt.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>Ph&acirc;n số</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Cắt một chiếc b&aacute;nh ngọt ra th&agrave;nh 4 phần hay cắt một quả t&aacute;o th&agrave;nh 4 miếng nhỏ... l&agrave; những c&aacute;ch gi&uacute;p b&eacute; học về ph&acirc;n số. Mẹ c&oacute; thể lấy một miếng (một phần tư) ra v&agrave; để lại 3 miếng. Chỉ cho b&eacute; thấy 2/4 bằng với một nửa. Tuần sau đ&oacute;, h&atilde;y cắt chiếc b&aacute;nh th&agrave;nh 3 phần, 6 phần, 8 phần v&agrave; giới thiệu tiếp với con về c&aacute;c ph&acirc;n số kh&aacute;c.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>Ph&eacute;p nh&acirc;n</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c b&eacute; tiểu học c&oacute; thể trau dồi khả năng l&agrave;m to&aacute;n với một chiếc b&aacute;nh ngọt mềm: Mỗi chiếc b&aacute;nh chia l&agrave;m 6 h&agrave;ng v&agrave; mỗi h&agrave;ng c&oacute; 5 &ocirc;. Cho b&eacute; thực hiện ph&eacute;p nh&acirc;n xem chiếc b&aacute;nh c&oacute; tất cả bao nhi&ecirc;u &ocirc; (6 x 5 = ?)</p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;c b&eacute; lớn hơn c&oacute; thể thực h&agrave;nh ph&eacute;p nh&acirc;n bằng c&aacute;ch tăng gấp đ&ocirc;i hoặc gấp ba lần một c&ocirc;ng thức nấu ăn: Chẳng hạn, mẹ cần một cốc bột v&agrave; 3/4 cốc nước để l&agrave;m được 12 chiếc b&aacute;nh. B&acirc;y giờ nếu mẹ muốn l&agrave;m 24, 36 hay 48 chiếc b&aacute;nh th&igrave; sẽ cần bao nhi&ecirc;u cốc nước v&agrave; bao nhi&ecirc;u cốc bột?</p>\r\n', '4334e26b459bad1d7be3a8428f2754db.jpg', '1402389882');
INSERT INTO `news` VALUES ('36', '8 bí mật của những bà mẹ có con ngoan', '8-bi-mat-cua-nhung-ba-me-co-con-ngoan', '3', 'Trẻ em cần được rèn luyện tính kỷ luật từ nhỏ, tốt nhất là ngay từ khi bé bắt đầu biết nhận thức. Điều này rất có ích khi trẻ trưởng thành.', '<p><strong>Trẻ em cần được r&egrave;n luyện t&iacute;nh kỷ luật từ nhỏ, tốt nhất l&agrave; ngay từ khi b&eacute; bắt đầu biết nhận thức. Điều n&agrave;y rất c&oacute; &iacute;ch khi trẻ trưởng th&agrave;nh.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>1. T&iacute;nh nhất qu&aacute;n l&agrave; ch&igrave;a kh&oacute;a</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">V&iacute; dụ bạn đặt ra quy tắc: &quot;Nếu đến giờ ăn, trẻ kh&ocirc;ng chịu ngồi v&agrave;o b&agrave;n th&igrave; sẽ kh&ocirc;ng được xem ti vi sau bữa ăn&quot; - v&agrave; nhất qu&aacute;n với nguy&ecirc;n tắc đ&oacute;. Điều n&agrave;y sẽ gi&uacute;p trẻ suy đo&aacute;n được c&aacute;ch cư xử của bạn để tr&aacute;nh n&eacute;.<br />\r\n<br />\r\nViệc kh&ocirc;ng thống nhất sẽ khiến trẻ cảm thấy kh&ocirc;ng chắc chắn, kh&ocirc;ng an to&agrave;n v&agrave; bối rối. &nbsp;<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>2. Lựa chọn sự ưu ti&ecirc;n</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Bạn cần biết chọn lựa th&aacute;ch thức kỉ luật n&agrave;o đầu ti&ecirc;n cần được giải quyết nếu bạn kh&ocirc;ng muốn m&igrave;nh bị qu&aacute; tải.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>3. Lu&ocirc;n lu&ocirc;n đặt m&igrave;nh v&agrave;o vị tr&iacute; của con để giải quyết vấn đề 1 c&aacute;ch ph&ugrave; hợp</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Đ&ocirc;i khi những &ocirc;ng bố b&agrave; mẹ lu&ocirc;n muốn con m&igrave;nh phải theo &yacute; họ v&agrave; h&agrave;nh xử theo c&aacute;ch của họ m&agrave; kh&ocirc;ng biết điều đ&oacute; ho&agrave;n to&agrave;n kh&ocirc;ng ph&ugrave; hợp với con m&igrave;nh. Điều n&agrave;y sẽ c&agrave;ng khiến việc kỷ luật con của bạn trở n&ecirc;n thất bại.</p>\r\n\r\n<p style=\"text-align:justify\"><strong><span style=\"color:#ff0000\">4. Tuyệt đối kh&ocirc;ng n&ecirc;n c&aacute;u giận, qu&aacute;t mắng trẻ</span></strong></p>\r\n\r\n<p style=\"text-align:justify\">C&aacute;ch tốt nhất bạn n&ecirc;n viết những điều sau v&agrave;o giấy nhớ v&agrave; d&aacute;n ch&uacute;ng ở những nơi dễ d&agrave;ng nh&igrave;n thấy: &quot;Kh&ocirc;ng c&atilde;i v&atilde; với con&quot;, &quot;Cần c&oacute; sự hợp t&aacute;c giữa 2 mẹ con&quot; hoặc &quot;Kh&ocirc;ng được c&aacute;u l&ecirc;n&quot;.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>5. T&igrave;m kiếm sự ủng hộ từ những người xung quanh</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Việc dạy con cần sự thống nhất tuyệt đối giữa c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh. Điều n&agrave;y c&oacute; nghĩa khi trẻ mắc lỗi sẽ c&oacute; nguy cơ bị rơi v&agrave;o sự c&ocirc; lập, gặp sự phản đối của tất cả mọi người v&agrave; kh&ocirc;ng c&oacute; đồng minh. Từ đ&oacute;, trẻ sẽ kh&ocirc;ng d&aacute;m t&aacute;i phạm.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>6. Tr&igrave; ho&atilde;n việc thực hiện quy tắc kỉ luật chứ kh&ocirc;ng n&ecirc;n bỏ qua</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Nếu trẻ mắc lỗi, l&uacute;c đ&oacute; bạn qu&aacute; bận rộn hoặc v&ocirc; t&igrave;nh qu&ecirc;n mất việc kỉ luật trẻ th&igrave; v&ocirc; h&igrave;nh chung, cũng sẽ khiến trẻ kh&ocirc;ng c&ograve;n t&ocirc;n trọng nguy&ecirc;n tắc của bạn. C&aacute;ch tốt nhất, trong trường hợp đ&oacute;, bạn n&ecirc;n tr&igrave; ho&atilde;n việc c&oacute; thể n&oacute;i: &quot;Mẹ kh&ocirc;ng đồng &yacute; với việc l&agrave;m của con vừa rồi. Ch&uacute;ng ta sẽ n&oacute;i chuyện về vấn đề n&agrave;y sau bữa ăn tối nay&quot;.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>7. Tuy nhi&ecirc;n, trong một số trường hợp bạn c&oacute; thể vẫn bỏ qua lỗi của trẻ, tuy nhi&ecirc;n h&atilde;y thể hiện đ&oacute; l&agrave; một ngoại lệ duy nhất v&agrave; n&ecirc;n th&ocirc;ng b&aacute;o sớm cho trẻ</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">C&oacute; thể n&oacute;i: &quot;V&igrave; tuần n&agrave;y con sẽ đến nh&agrave; &ocirc;ng b&agrave; ngoại n&ecirc;n con c&oacute; thể bỏ qua việc dọn dẹp ph&ograve;ng m&igrave;nh cho đến tuần tới.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>8. Bạn cần ki&ecirc;n tr&igrave; tuyệt đối trong việc dạy con, nhất l&agrave; khi kỉ luật trẻ</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">C&oacute; nhiều b&agrave; mẹ trước đ&oacute; c&oacute; thể rất tức giận con nhưng đ&atilde; mềm l&ograve;ng ngay khi con kh&oacute;c l&oacute;c qu&aacute; nhiều v&agrave; bỏ ngay c&aacute;c quy tắc dạy trẻ. Điều n&agrave;y sẽ khiến trẻ c&agrave;ng tạo &aacute;p lực cho bạn. C&aacute;ch tốt nhất khi trẻ kh&oacute;c l&oacute;c, bạn h&atilde;y để trẻ 1 m&igrave;nh ở trong ph&ograve;ng. Khi kh&ocirc;ng c&oacute; ai, trẻ sẽ sớm n&iacute;n.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '2e43913ffb335a03c9ad9f5b56b796a4.jpg', '1402389958');
INSERT INTO `news` VALUES ('37', 'Dạy bé mẫu giáo cách nhận diện các loại quả', 'Day-be-mau-giao-cach-nhan-dien-cac-loai-qua', '3', 'Trước tiên, bạn có thể chọn cùng một loại quả nhưng có màu sắc khác nhau; chẳng hạn, táo xanh - táo đỏ, dưa vàng - dưa đỏ, nho xanh - nho tím… Đồng thời, bạn cũng có thể chọn những loại quả khác nhau nhưng có màu sắc tương đồng như quả cam và quả quýt, quả mận đỏ và quả nho đỏ… Sau đó, bạn xếp các loại quả này trên cùng một đĩa.\r\n', '<p><strong>Những hoạt động vừa gi&uacute;p b&eacute; biết ph&acirc;n biệt c&aacute;c loại quả vừa c&oacute; t&aacute;c dụng khuyến kh&iacute;ch b&eacute; th&iacute;ch ăn hoa quả hơn.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>Gi&uacute;p b&eacute; nhận diện hoa quả theo m&agrave;u sắc</strong></span><br />\r\n<br />\r\nTrước ti&ecirc;n, bạn c&oacute; thể chọn c&ugrave;ng một loại quả nhưng c&oacute; m&agrave;u sắc kh&aacute;c nhau; chẳng hạn, t&aacute;o xanh - t&aacute;o đỏ, dưa v&agrave;ng - dưa đỏ, nho xanh - nho t&iacute;m&hellip; Đồng thời, bạn cũng c&oacute; thể chọn những loại quả kh&aacute;c nhau nhưng c&oacute; m&agrave;u sắc tương đồng như quả cam v&agrave; quả qu&yacute;t, quả mận đỏ v&agrave; quả nho đỏ&hellip; Sau đ&oacute;, bạn xếp c&aacute;c loại quả n&agrave;y tr&ecirc;n c&ugrave;ng một đĩa.<br />\r\n<br />\r\nBạn y&ecirc;u cầu b&eacute; &ldquo;Lấy cho mẹ quả t&aacute;o&rdquo; hoặc &ldquo;Lấy cho mẹ những quả c&oacute; m&agrave;u đỏ&rdquo;&hellip; B&eacute; sẽ ph&acirc;n biệt được hai kh&aacute;i niệm:<br />\r\n<br />\r\n- Kh&aacute;i niệm thứ nhất, c&ugrave;ng một loại quả nhưng c&oacute; nhiều m&agrave;u sắc kh&aacute;c nhau.<br />\r\n<br />\r\n- Kh&aacute;i niệm thứ hai, c&ugrave;ng một m&agrave;u nhưng c&oacute; nhiều loại quả kh&aacute;c nhau.</p>\r\n\r\n<p style=\"text-align:justify\">Tr&ograve; chơi cầu vồng sắc m&agrave;u: Bạn c&oacute; thể sử dụng m&agrave;u v&agrave;ng của dứa, xo&agrave;i; m&agrave;u cam của cam, qu&yacute;t, c&agrave; rốt; m&agrave;u đỏ của d&acirc;u t&acirc;y, dưa hấu; m&agrave;u xanh của quả kiwi; m&agrave;u trắng của sữa chua. Bạn xếp c&aacute;c loại quả đ&atilde; được cắt miếng th&agrave;nh h&igrave;nh cầu vồng tr&ecirc;n một chiếc đĩa thật lớn. Sau đ&oacute;, bạn đưa cho b&eacute; một miếng hoa quả v&agrave; y&ecirc;u cầu b&eacute; gọi t&ecirc;n. Nếu b&eacute; trả lời ch&iacute;nh x&aacute;c, b&eacute; sẽ được ph&eacute;p &ldquo;măm măm&rdquo; m&oacute;n quả n&agrave;y.<br />\r\n<br />\r\nB&eacute; sẽ biết c&aacute;ch nhận diện hoa quả cho d&ugrave; ch&uacute;ng được th&aacute;i l&aacute;t. Ngo&agrave;i ra, b&eacute; c&ograve;n được l&agrave;m quen với m&oacute;n sữa chua nh&uacute;ng hoa quả ngon miệng.<br />\r\n<br />\r\nTr&ograve; chơi gh&eacute;p hoa quả theo tranh: Trước ti&ecirc;n, bạn chuẩn bị một giỏ hoa quả với nhiều loại kh&aacute;c nhau như cam, t&aacute;o, l&ecirc;, dưa hấu, d&acirc;u t&acirc;y&hellip; Đồng thời, bạn để sẵn một quyển s&aacute;ch c&oacute; h&igrave;nh minh họa c&aacute;c loại quả b&ecirc;n cạnh. Khi bạn chỉ tay v&agrave;o một h&igrave;nh trong s&aacute;ch, b&eacute; sẽ tự chọn một loại quả trong giỏ sao cho ph&ugrave; hợp.<br />\r\n<br />\r\nB&eacute; sẽ nhận diện được những loại hoa quả c&oacute; trong s&aacute;ch v&agrave; những loại hoa quả ngo&agrave;i đời thực.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>Gi&uacute;p b&eacute; nhận diện hoa quả theo m&ugrave;i vị</strong></span><br />\r\n<br />\r\nBạn c&oacute; thể chế biến cam theo 2 kiểu: cắt th&agrave;nh miếng v&agrave; vắt th&agrave;nh nước cam để cho b&eacute; uống. Tiếp đến, bạn pha th&ecirc;m một cốc nước chanh (hoặc một cốc nước hoa quả kh&aacute;c) v&agrave; để cạnh nhau.<br />\r\n<br />\r\nBạn c&oacute; thể gợi &yacute; với b&eacute;: &ldquo;Mẹ con m&igrave;nh c&ugrave;ng chơi tr&ograve; b&iacute; mật với hoa quả nh&eacute;&rdquo;. Sau đ&oacute;, bạn gợi &yacute; để b&eacute; uống một ngụm nước cam v&agrave; một ngụm nước chanh. Cuối c&ugrave;ng, bạn mới cho b&eacute; ăn cam v&agrave; đưa ra c&acirc;u hỏi trắc nghiệm với b&eacute;: &ldquo;Đố con biết, m&ugrave;i vị của miếng cam n&agrave;y giống với cốc nước n&agrave;o con vừa uống nhất?&rdquo;.<br />\r\n<br />\r\nC&aacute;ch n&agrave;y cũng c&oacute; t&aacute;c dụng lớn trong việc k&iacute;ch th&iacute;ch b&eacute; ăn hoa quả. Nhiều cha mẹ tin việc chế biến hoa quả th&agrave;nh những m&oacute;n đẹp mắt sẽ l&ocirc;i cuốn được b&eacute; m&agrave; họ qu&ecirc;n rằng, dạy b&eacute; c&aacute;ch ph&acirc;n biệt m&ugrave;i vị thực phẩm cũng khiến b&eacute; th&iacute;ch th&uacute;.</p>\r\n', 'f2d673afde06dd804c95fc6906118e4e.jpg', '1402390010');
INSERT INTO `news` VALUES ('38', 'Thực đơn của bé tại Mầm non Bảo Ngọc, tuần 1 và 2-tháng 6/2014', 'Thuc-don-cua-be-tai-Mam-non-Bao-Ngoc-tuan-1-va-2-thang-6-2014', '4', 'Thực đơn của bé tại Mầm non Bảo Ngọc, tuần 1--THÁNG 6/2014', '<table style=\"border-color:rgb(225, 225, 225); border-style:solid; font-size:16px; text-align:center\">\r\n	<thead>\r\n		<tr>\r\n			<td colspan=\"2\">Ng&agrave;y</td>\r\n			<td rowspan=\"2\">Thứ 2</td>\r\n			<td rowspan=\"2\">Thứ 3</td>\r\n			<td rowspan=\"2\">Thứ 4</td>\r\n			<td rowspan=\"2\">Thứ 5</td>\r\n			<td rowspan=\"2\">Thứ 6</td>\r\n			<td rowspan=\"2\">Thứ 7</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">Bữa ăn</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">S&aacute;ng</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">Phụ s&aacute;ng</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Trưa</td>\r\n			<td>Mặn</td>\r\n			<td>Thứ 3</td>\r\n			<td>Thứ 4</td>\r\n			<td>Thứ 5</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Canh</td>\r\n			<td>Thứ 3</td>\r\n			<td>Thứ 4</td>\r\n			<td>Thứ 5</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">Chiều</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n		</tr>\r\n		<tr>\r\n			<td colspan=\"2\">Phụ chiều</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n			<td>Bữa ăn</td>\r\n		</tr>\r\n		<tr>\r\n			<td rowspan=\"2\">Tối</td>\r\n			<td>Mặn</td>\r\n			<td>Thứ 3</td>\r\n			<td>Thứ 4</td>\r\n			<td>Thứ 5</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n		</tr>\r\n		<tr>\r\n			<td>Canh</td>\r\n			<td>Thứ 3</td>\r\n			<td>Thứ 4</td>\r\n			<td>Thứ 5</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n			<td>Thứ 6</td>\r\n		</tr>\r\n	</thead>\r\n</table>\r\n', '5681076c4979d84d9933c1d645cb68ed.jpg', '1402391267');
INSERT INTO `news` VALUES ('39', '8 cách để không bị bé làm phiền khi đang bận việc', '8-cach-de-khong-bi-be-lam-phien-khi-dang-ban-viec', '3', 'Nhà chỉ có hai mẹ con, bạn thì đang bận với việc nội trợ, bé (4, 5 tuổi) lại luôn miệng: \"Mẹ ơi, con buồn lắm, mẹ chơi với con đi!\".', '<p style=\"text-align:justify\"><strong>Nh&agrave; chỉ c&oacute; hai mẹ con, bạn th&igrave; đang bận với việc nội trợ, b&eacute; (4, 5 tuổi) lại lu&ocirc;n miệng: &quot;Mẹ ơi, con buồn lắm, mẹ chơi với con đi!&quot;.</strong></p>\r\n\r\n<p style=\"text-align:justify\">L&uacute;c n&agrave;y, bạn cần t&igrave;m cho b&eacute; một số hoạt động để m&igrave;nh rảnh rang l&agrave;m việc của m&igrave;nh v&agrave; dưới đ&acirc;y l&agrave; một số gợi &yacute;:<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>1. Sắp xếp nh&agrave; cửa</strong></span><br />\r\n<br />\r\nBạn c&oacute; thể hướng dẫn cho con c&aacute;ch thu dọn ph&ograve;ng ri&ecirc;ng của b&eacute;. Gợi &yacute; rằng, b&eacute; c&oacute; thể sắp xếp v&agrave; trang tr&iacute; ph&ograve;ng theo &yacute; th&iacute;ch. Sau đ&oacute;, bạn sẽ quay lại kiểm tra kết quả v&agrave; tặng cho b&eacute; một phần thưởng xứng đ&aacute;ng.<br />\r\n<br />\r\nTốt nhất, bạn c&oacute; thể chuẩn bị một chậu nước, một chiếc khăn v&agrave; động vi&ecirc;n b&eacute; lau b&agrave;n ghế, giường tủ, s&agrave;n nh&agrave;... Bạn n&ecirc;n động vi&ecirc;n để b&eacute; h&agrave;o hứng với c&ocirc;ng việc n&agrave;y.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>2. Mời bạn của b&eacute; đến nh&agrave;</strong></span><br />\r\n<br />\r\nNếu b&eacute; c&oacute; một v&agrave;i người bạn c&ugrave;ng độ tuổi sinh sống gần nh&agrave;, bạn c&oacute; thể gọi điện mời bạn của b&eacute; đến nh&agrave; chơi. B&eacute; sẽ h&agrave;o hứng vui chơi c&ugrave;ng bạn b&egrave; v&agrave; kh&ocirc;ng ở b&ecirc;n cạnh l&agrave;m phiền bạn nữa.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>3. Những &acirc;m thanh vui vẻ</strong></span><br />\r\n<br />\r\nBật cho b&eacute; nghe một c&acirc;u chuyện tr&ecirc;n radio d&agrave;nh cho thiếu nhi hoặc bạn c&oacute; thể mở một chiếc đĩa c&oacute; những b&agrave;i h&aacute;t vui vẻ b&eacute; y&ecirc;u th&iacute;ch.<br />\r\n<br />\r\nBạn chỉ n&ecirc;n mở đ&agrave;i hoặc đĩa CD chứ kh&ocirc;ng phải tivi hay m&aacute;y vi t&iacute;nh. V&igrave; nếu bạn phải l&agrave;m việc nh&agrave; trong một thời gian d&agrave;i sẽ kh&oacute; kiểm so&aacute;t được thời gian b&eacute; xem tivi.<br />\r\n<br />\r\nTiếp đến, bạn c&oacute; thể đưa cho b&eacute; giấy v&agrave; b&uacute;t m&agrave;u để b&eacute; vẽ lại tranh theo nội dung của c&acirc;u chuyện hay b&agrave;i h&aacute;t m&agrave; b&eacute; vừa nghe được.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>4. Chơi dựng lều</strong></span><br />\r\n<br />\r\nTrước ti&ecirc;n, bạn cần chuẩn bị một chiếc chiếu cũ: Uốn cong một m&eacute;p chiếu v&agrave;o tường v&agrave; d&ugrave;ng chăn chặn một b&ecirc;n m&eacute;p chiếu c&ograve;n lại. L&uacute;c n&agrave;y chiếc chiếu đ&atilde; tạo th&agrave;nh một lỗ hổng giống như một chiếc lều nhỏ để b&eacute; c&oacute; thể chui ra chui v&agrave;o đ&ugrave;a nghịch.<br />\r\n<br />\r\nBạn c&oacute; thể mang cho b&eacute; ch&uacute;t đồ chơi, &iacute;t đồ ăn nhẹ để b&eacute; sử dụng trong lều.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>5. Đạp xe</strong></span><br />\r\n<br />\r\nNếu nh&agrave; bạn c&oacute; ph&ograve;ng rộng, bạn c&oacute; thể cho b&eacute; đạp xe l&ograve;ng v&ograve;ng quanh nh&agrave;. Để b&eacute; th&uacute; vị hơn, bạn c&oacute; thể cho b&eacute; v&agrave;o vai người giao h&agrave;ng, chằng hạn nhờ b&eacute; chờ chiếc cốc từ ph&ograve;ng kh&aacute;ch sang ph&ograve;ng bếp&hellip;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>6. S&aacute;ng tạo với hoa quả</strong></span><br />\r\n<br />\r\nTrước hết, bạn c&oacute; thể cắt l&aacute;t những miếng dưa chuột, t&aacute;o, d&acirc;u t&acirc;y&hellip; sau đ&oacute;, xếp những l&aacute;t hoa quả n&agrave;y ra đĩa v&agrave; đưa cho b&eacute;. Hướng dẫn b&eacute; b&agrave;y đặt v&agrave; trang tr&iacute; những l&aacute;t hoa quả n&agrave;y th&agrave;nh h&igrave;nh b&ocirc;ng hoa hay h&igrave;nh mặt trời hay bất kỳ một mẫu h&igrave;nh n&agrave;o khiến b&eacute; th&iacute;ch th&uacute;.<br />\r\n<br />\r\nNếu kh&ocirc;ng bạn cũng c&oacute; thể kiếm một v&agrave;i vỏ trứng, rửa sạch v&agrave; cho b&eacute; vẽ h&igrave;nh mặt hề l&ecirc;n đ&oacute;.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>7. Truy t&igrave;m kho b&aacute;u</strong></span><br />\r\n<br />\r\nBạn cất giấu một đồ vật m&agrave; b&eacute; y&ecirc;u th&iacute;ch như đồ chơi hay một quyến s&aacute;ch. Tiếp đến, bạn vẽ một sơ đồ minh họa để b&eacute; tự biết c&aacute;ch t&igrave;m kho b&aacute;u &ndash; đồ vật ấy.<br />\r\n<br />\r\nĐể b&eacute; bất ngờ v&agrave; hứng th&uacute;, bạn c&oacute; thể vẽ bản đồ chỉ dẫn hơi rắc rối một ch&uacute;t. V&iacute; dụ, muốn đến được &ldquo;kho b&aacute;u&rdquo; b&eacute; phải t&igrave;m đủ 2 chiếc &ldquo;ch&igrave;a kh&oacute;a&rdquo; - l&agrave; hai đồ chơi kh&aacute;c được giấu trong nh&agrave;.<br />\r\n<br />\r\nBạn cũng c&oacute; thể chọn tờ giấy cũ, x&eacute; m&eacute;p để vẽ bản đồ cho c&oacute; vẻ &ldquo;thần b&iacute;&rdquo; như trong những c&acirc;u chuyện b&eacute; đ&atilde; được nghe.<br />\r\n<br />\r\n<span style=\"color:#ff0000\"><strong>8. Nh&agrave; thiết kế thời trang</strong></span><br />\r\n<br />\r\nĐưa cho b&eacute; một &iacute;t vải vụn, v&agrave;i c&ocirc; b&uacute;p b&ecirc;, một c&aacute;i k&eacute;o nhỏ v&agrave;i c&aacute;i b&uacute;t m&agrave;u v&agrave; hướng dẫn b&eacute; cắt &aacute;o v&aacute;y cho b&uacute;p b&ecirc;. Tr&ograve; chơi n&agrave;y rất được c&aacute;c b&eacute; g&aacute;i ưa chuộng; tuy nhi&ecirc;n, nếu l&agrave; b&eacute; trai, bạn c&oacute; thể khuyến kh&iacute;ch b&eacute; vẽ &aacute;o cho si&ecirc;u nh&acirc;n.<br />\r\n<br />\r\nNgo&agrave;i ra, bạn cũng c&oacute; thể t&igrave;m một cuốn tạp ch&iacute; thời trang v&agrave; dạy b&eacute; cắt, gh&eacute;p tranh, tạo ra những bộ v&aacute;y &aacute;o mới theo &yacute; th&iacute;ch của b&eacute;.<br />\r\n<br />\r\nLưu &yacute;: Chọn loại k&eacute;o thủ c&ocirc;ng d&agrave;nh cho b&eacute; v&agrave; lu&ocirc;n để mắt đến con.</p>\r\n', '94d0e2eb4e3d8fc83f4069b167d57373.jpg', '1402390103');
INSERT INTO `news` VALUES ('40', '\"Giải thoát\" con yêu khỏi chứng biếng ăn bằng 10 mẹo đơn giản', 'Giai-thoat-con-yeu-khoi-chung-bieng-an-bang-10-meo-don-gian', '3', 'Bạn đừng áp dụng một cách máy móc những thực đơn cho trẻ được giới thiệu trong các cuốn sách hướng dẫn hay các bài chia sẻ. Bởi mỗi đứa trẻ lại có một nhu cầu, một sở thích và khẩu vị khác nhau nên không thể áp dụng hoàn toàn từ đứa trẻ này sang đứa trẻ kia. Hãy giới thiệu với con những món ăn mà con thích ngay từ đầu, bên cạnh đó mẹ nên chế biến đồ ăn theo nhiều cách khác nhau đã đa dạng hóa bữa ăn của con. Điều này sẽ giúp cho chính bạn không bị rơi vào lối mòn những thực đơn dinh dưỡng cho con mình.', '<p style=\"text-align:justify\"><strong>Việc l&agrave;m thế n&agrave;o để gi&uacute;p con ăn ngon miệng, l&agrave;m thế n&agrave;o để con h&agrave;o hứng với những bữa ăn lu&ocirc;n l&agrave; c&acirc;u hỏi đặt ra cho mỗi b&agrave; mẹ. 10 bước nhỏ sau đ&acirc;y sẽ gi&uacute;p con bạn tho&aacute;t khỏi t&igrave;nh trạng biếng ăn.</strong></p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>1.&nbsp;Mở rộng thực đơn cho con</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Bạn đừng &aacute;p dụng một c&aacute;ch m&aacute;y m&oacute;c những thực đơn cho trẻ được giới thiệu trong c&aacute;c cuốn s&aacute;ch hướng dẫn hay c&aacute;c b&agrave;i chia sẻ. Bởi mỗi đứa trẻ lại c&oacute; một nhu cầu, một sở th&iacute;ch v&agrave; khẩu vị kh&aacute;c nhau n&ecirc;n kh&ocirc;ng thể &aacute;p dụng ho&agrave;n to&agrave;n từ đứa trẻ n&agrave;y sang đứa trẻ kia. H&atilde;y giới thiệu với con những m&oacute;n ăn m&agrave; con th&iacute;ch ngay từ đầu, b&ecirc;n cạnh đ&oacute; mẹ n&ecirc;n chế biến đồ ăn theo nhiều c&aacute;ch kh&aacute;c nhau đ&atilde; đa dạng h&oacute;a bữa ăn của con. Điều n&agrave;y sẽ gi&uacute;p cho ch&iacute;nh bạn kh&ocirc;ng bị rơi v&agrave;o lối m&ograve;n những thực đơn dinh dưỡng cho con m&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>2.&nbsp;H&atilde;y đưa con đi mua thực phẩm c&ugrave;ng bạn</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; c&aacute;ch v&ocirc; c&ugrave;ng hiệu quả, bởi khi bạn đưa con đi c&ugrave;ng, cho con được ph&eacute;p lựa chọn bạn sẽ hiểu r&otilde; hơn sở th&iacute;ch của con. Đồng thời qua đ&oacute; bạn cũng c&oacute; thể dạy con đ&acirc;u l&agrave; nh&oacute;m thực phẩm tốt đ&acirc;u l&agrave; nh&oacute;m thực phẩm kh&ocirc;ng tốt cho con. Đọc to th&agrave;nh phần dinh dưỡng trong c&aacute;c loại thực phẩm đ&oacute;ng g&oacute;i cũng l&agrave; c&aacute;ch gi&uacute;p con c&oacute; th&ecirc;m kiến thức về dinh dưỡng sau n&agrave;y. N&ecirc;n nhớ rằng th&oacute;i quen tốt của con phải bắt đầu từ ch&iacute;nh th&oacute;i quen tốt của bạn.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong><strong>3.&nbsp;Cho con tham gia v&agrave;o việc l&ecirc;n thực đơn</strong></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">H&atilde;y để con được quyết định hay đ&oacute;ng g&oacute;p &yacute; kiến v&agrave;o ch&iacute;nh thực đơn của gia đ&igrave;nh m&igrave;nh. H&ocirc;m nay n&ecirc;n ăn m&oacute;n g&igrave;? Khi n&agrave;o th&igrave; ăn thực phẩm n&agrave;y? v.v&hellip; Việc để con sắp xếp v&agrave; đưa ra thực đơn cho gia đ&igrave;nh m&igrave;nh sẽ gi&uacute;p con h&agrave;o hứng v&agrave; thấy được vai tr&ograve; của con l&agrave; &ldquo;v&ocirc; c&ugrave;ng quan trọng&rdquo;. Khi đ&oacute; con sẽ lại c&agrave;ng h&agrave;o hứng với mỗi bữa ăn hơn bao giờ hết.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong><strong>4.&nbsp;Ph&acirc;n c&ocirc;ng nhiệm vụ trong bếp</strong></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">T&ugrave;y từng độ tuổi m&agrave; bạn c&oacute; thể ph&acirc;n c&ocirc;ng con l&agrave;m những việc trong bếp, c&ugrave;ng bạn chuẩn bị bữa tối cho cả gia đ&igrave;nh. Trẻ con rất mong muốn được người lớn ghi nhận những đ&oacute;ng g&oacute;p của m&igrave;nh ch&iacute;nh v&igrave; vậy khi được thực hiện nhiệm vụ n&agrave;y con sẽ thấy m&igrave;nh được đề cao, m&igrave;nh cũng g&oacute;p phần v&agrave;o việc tổ chức bữa cơm cho gia đ&igrave;nh. Kết quả l&agrave; con sẽ ăn ngon miệng hơn.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong><strong>5.&nbsp;Thử kết hợp những thực phẩm mới với thực phẩm đ&atilde; quen thuộc</strong></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Bằng c&aacute;ch n&agrave;y sẽ gi&uacute;p bạn kiểm nghiệm việc con c&oacute; h&agrave;o hứng với một loại thực phẩm mới hay kh&ocirc;ng. Đừng qu&aacute; vội v&agrave;ng trong việc &aacute;p dụng những thực đơn mới ho&agrave;n to&agrave;n cho con điều đ&oacute; hết sức sai lầm. Thử nghiệm dần dần, từng bước một khi thấy con đ&atilde; quen v&agrave; thấy ngon miệng với những thực phẩm đ&oacute; sẽ l&agrave; l&uacute;c bạn tăng phần ăn đ&oacute; l&ecirc;n nhiều hơn.</p>\r\n\r\n<p style=\"text-align:justify\"><strong>&nbsp;</strong><span style=\"color:#ff0000\"><strong><strong>6.&nbsp;Truyền cho con t&igrave;nh y&ecirc;u nấu nướng</strong></strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Khi c&oacute; t&igrave;nh y&ecirc;u với việc nấu nướng n&oacute; sẽ gi&uacute;p lan tỏa t&igrave;nh y&ecirc;u sang c&aacute;c m&oacute;n ăn v&agrave; khi đ&oacute; việc lo lắng con biếng ăn chẳng c&ograve;n l&agrave; vấn đề nữa. Tập cho con bằng c&aacute;ch c&ugrave;ng con chơi những tr&ograve; chơi li&ecirc;n quan tới bếp n&uacute;c, những bộ đồ nấu bếp nho nhỏ v&agrave; c&ugrave;ng giả vờ nấu nướng với con sẽ khiến con v&ocirc; c&ugrave;ng th&iacute;ch th&uacute; đấy!</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>7.&nbsp;Dạy con những điều cơ bản</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Việc rửa tay trước khi ăn hay việc rau củ quả phải rửa sạch trước khi chế biến, rau muống phải nhặt như thế n&agrave;o? Khoai t&acirc;y muốn nấu ch&iacute;n phải gọt vỏ ra sao? Tất cả những điều n&agrave;y sẽ gi&uacute;p con l&agrave;m quen với từng h&agrave;nh động cụ thể c&oacute; li&ecirc;n quan tới bữa ăn cũng sẽ g&oacute;p phần l&agrave;m con thấy h&agrave;o hứng mỗi khi bữa ăn đến.</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>8.&nbsp;Tự tay trồng c&aacute;c loại rau củ</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Nếu c&oacute; điều kiện, h&atilde;y cho ph&eacute;p con c&ugrave;ng trồng một loại rau củ g&igrave; đ&oacute; trong vườn nh&agrave; hoặc trong một khoảng đất trống. Kh&ocirc;ng cần cầu kỳ, chỉ l&agrave; con c&oacute; thể chứng kiến sự ph&aacute;t triển của c&aacute;c loại c&acirc;y cũng sẽ gi&uacute;p con hứng th&uacute; với c&aacute;c bữa ăn hơn rất nhiều.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>9.&nbsp;D&agrave;nh cho con những m&oacute;n ăn tươi</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Thanh v&igrave; d&ugrave;ng c&aacute;c thực phẩm đ&oacute;ng hộp h&atilde;y để con ăn nhiều c&aacute;c loại rau củ tươi, c&aacute;c loại ngũ cốc nguy&ecirc;n c&aacute;m thay v&igrave; đ&atilde; tinh luyện điều n&agrave;y kh&ocirc;ng chỉ gi&uacute;p s&aacute;ng tạo ra nhiều m&oacute;n ăn kh&aacute;c nhau m&agrave; đồng thời cũng sẽ gi&uacute;p tăng cường hệ miễn dịch cho con.&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\"><span style=\"color:#ff0000\"><strong>10.&nbsp;Đưa con đi thăm nơi trực tiếp sản xuất ra c&aacute;c loại thực phẩm</strong></span></p>\r\n\r\n<p style=\"text-align:justify\">Vi&ecirc;c đưa con đến thăm c&aacute;c c&aacute;nh đồng l&uacute;a, hay n&ocirc;ng trường b&ograve; sữa, hay trang trại nu&ocirc;i g&agrave;&hellip; tất cả để con hiểu hơn về quy tr&igrave;nh c&oacute; được c&aacute;c loại thực phẩm con sử dụng h&agrave;ng ng&agrave;y ra sao. N&oacute; sẽ gi&uacute;p con c&oacute; c&aacute;i nh&igrave;n cụ thể hơn về thực phẩm v&agrave; thấy tr&acirc;n trọng những thực phẩm con được ăn h&agrave;ng ng&agrave;y.</p>\r\n\r\n<p>&nbsp;</p>\r\n', '8e6b540af0df24c884ed9c09f646aa02.jpg', '1402390251');
